	f.v.push_back( make_pair(ni, nj) );
	if( puz[ni][nj] == '.' ) filled = false;
	puz[ni][nj] = '#';
      }
      if( 1 < f.v.size() ){
	if( !filled ) v[v_size++] = f;
	else{
	  if( !check_syntax( build( f ) ) )return false;
	}
      }
    }
  }  


  if( v_size == 0 )return true;

  copy( g[0], g[H], puz[0] );
  for(int i=0; i<h; ++i){
    for(int j=0; j<w; ++j){
      if( puz[i][j] == '#' )continue;

      bool filled = true;
      F f;
      for(int l=0; ; ++l){
	int ni = i + l;
	int nj = j;
	if( h <= ni || w <= nj )break;
	if( puz[ni][nj] == '#' )break;
	f.v.push_back( make_pair(ni, nj) );
	if( puz[ni][nj] == '.' ) filled = false;
	puz[ni][nj] = '#';
      }
      if( 1 < f.v.size() ){
	if( !filled ) v[v_size++] = f;
	else{
	  if( !check_syntax( build( f ) ) )return false;
	}
      }
    }
  }

  sort( p, p + size );
  do{

    for(int i=0; i<emp_size; ++i){
      g[ emp[i].first ][ emp[i].second ] = p[i];
    }
    bool flg = true;
    for(int i=0; i<v_size && flg; ++i){
      string s;
      for(int j=0; j<v[i].v.size(); ++j){
	int a = v[i].v[j].first;
	int b = v[i].v[j].second;
	s += g[a][b];
      }
      if( !check_syntax(s) ) flg = false;
    }    
    if( flg ) return true;

  }while( next_permutation( p, p + size ) );

  return false;
}

