    return judge();
  }

  for(int i = 0; i < N; ++i) {
    int x = V[i].first;
    int y = V[i].second;
    if(M[y][x] != '.') continue;
    int type = -1; // 0:Num, 1:Operator
    for(int i = 0; i < 4; ++i) {
      int nx = x + dx[i];
      int ny = y + dy[i];
      if(nx < 0 || nx >= W) continue;
      if(ny < 0 || ny >= H) continue;
      if(M[ny][nx] != '#' && M[ny][nx] != '.') {
	if(!isdigit(M[ny][nx])) {
	  type = 1;
	  break;
	}
      }
    }
    if(!isdigit(T[t]) && type == 1) continue;

    M[y][x] = T[t];
    if(rec(t+1)) return true;
    M[y][x] = '.';
  }
  return false;
}

int main() {
  while((cin >> H >> W) && (H || W)) {
    V.clear();
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
	cin >> M[i][j];
	if(M[i][j] == '.') V.push_back(P(j, i));
      }
    }

    cin >> N;
    for(int i = 0; i < N; ++i) cin >> T[i];

    if(rec(0)) cout << "Yes" << endl;
    else        cout << "No" << endl;
  }
  return 0;
}

