bool sintaxCheck(string s){
    rep(i,s.size() - 1){
        if(not isdigit(s[i]) && not isdigit(s[i + 1])) return false;
        if(s[i] == '0' && isdigit(s[i + 1])) return false;
    }
    if(not isdigit(s.front()) || not isdigit(s.back())) return false;

    State tmp = s.begin();
    bool ans = eq(tmp);
    assert(tmp == s.end() || end_parse);
    return ans;
}

