using namespace std;
typedef pair<int, int> pii;

const int TYPE_EQUATION = 1;
const int TYPE_EXPR = 2;

struct Result {
	Result(int _t, int _v = 0, int _p = 0) : t(_t), v(_v), p(_p) { }
	int t, v, p;
};

#define PARSER_DECL(name) Result name (const char *s, int p, int d)

PARSER_DECL(equation);
PARSER_DECL(expr);
PARSER_DECL(term);
PARSER_DECL(number);

PARSER_DECL(equation){
	Result r = expr(s, p, d);
	if(s[r.p] != '='){ throw "equals not found"; }
	Result q = expr(s, r.p + d, d);
	if(s[q.p] != '#'){ throw "equation is infinished"; }
	return Result(TYPE_EQUATION, r.v == q.v, q.p);
}
PARSER_DECL(expr){
	Result r = term(s, p, d);
	while(s[r.p] == '+' || s[r.p] == '-'){
		Result q = term(s, r.p + d, d);
		if(s[r.p] == '+'){
			r.v += q.v;
		}else{
			r.v -= q.v;
		}
		r.p = q.p;
	}
	return r;
}
PARSER_DECL(term){
	Result r = number(s, p, d);
	while(s[r.p] == '*' || s[r.p] == '/'){
		Result q = number(s, r.p + d, d);
		if(s[r.p] == '*'){
			r.v *= q.v;
		}else{
			if(q.v == 0){ throw "zero division"; }
			if(r.v % q.v != 0){ throw "can't divide"; }
			r.v /= q.v;
		}
		r.p = q.p;
	}
	return r;
}
PARSER_DECL(number){
	if(!isdigit(s[p])){ throw "invalid number"; }
	Result r(TYPE_EXPR, 0, p);
	while(isdigit(s[r.p])){
		r.v = r.v * 10 + s[r.p] - '0';
		r.p += d;
	}
	if(r.p > p + d && s[p] == '0'){ throw "leading zero"; }
	return r;
}

int main(){
	while(true){
		char field[11 * 11];
		int H, W;
		cin >> H >> W;
		if(H == 0 && W == 0){ break; }
		vector<int> blanks;
		for(int i = 0; i < H; ++i){
			char *p = field + i * 11;
			cin >> p;
			p[W] = '#';
			for(int j = 0; j < W; ++j){
				if(p[j] == '.'){ blanks.push_back(i * 11 + j); }
			}
		}
		for(int i = 0; i <= W; ++i){
			field[H * 11 + i] = '#';
		}
		vector<pii> heads;
		for(int i = 0; i < H; ++i){
			char *l0 = field + (i - 1) * 11;
			char *l1 = l0 + 11, *l2 = l1 + 11, *l3 = l2 + 11;
			for(int j = 0; j < W; ++j){
				if(l1[j] == '#'){ continue; }
				if(i < H - 2){
					bool isHead = (i == 0 || l0[j] == '#');
					if(isHead && l2[j] != '#' && l3[j] != '#'){
						heads.push_back(pii(i * 11 + j, 11));
					}
				}
				if(j < W - 2){
					bool isHead = (j == 0 || l1[j - 1] == '#');
					if(isHead && l1[j + 1] != '#' && l1[j + 2] != '#'){
						heads.push_back(pii(i * 11 + j, 1));
					}
				}
			}
		}
		int n;
		cin >> n;
		vector<char> tokens(n);
		for(int i = 0; i < n; ++i){
			string s;
			cin >> s;
			tokens[i] = s[0];
		}
		sort(tokens.begin(), tokens.end());
		bool accept = false;
		do {
			for(int i = 0; i < n; ++i){ field[blanks[i]] = tokens[i]; }
			bool valid = true;
			for(int i = 0; i < heads.size(); ++i){
				try {
					Result r = equation(field, heads[i].first, heads[i].second);
					if(r.v == 0){
						valid = false;
						break;
					}
				}catch(const char *verdict){
					valid = false;
					break;
				}
			}
			if(valid){
				accept = true;
				break;
			}
		} while(next_permutation(tokens.begin(), tokens.end()));
		cout << (accept ? "Yes" : "No") << endl;
	}
	return 0;
}

