using namespace std;
typedef pair<int,int> result;

const int OutOfIntExc = 1,
          NegIntExc = 2,
          NotEquationExc = 3,
          NotNumExc = 4,
          LackNumExc = 5,
          IterExceedExc = 6;

result equation(const string &s, int p = 0);
result expression(const string &s, int p = 0);
result factor(const string &s, int p = 0);
result term(const string &s, int p = 0);

result equation(const string &s, int p) {
//  cout << "? " << s << " ";
  if (s[p] == '=') { throw NotEquationExc; }
  result r = expression(s, p);
  if (s[r.p] != '=') { throw NotEquationExc; }

  r.p++; // skip '=';
  result t = expression(s, r.p);
  t.value = (int)(r.value == t.value);

//  cout << (t.value ? "T" : "F") << endl;

  return t;
}


result expression(const char* &s, int p) {
  result r = factor(s, p);
  while (s[r.p] == '+' or s[r.p] == '-') {
    result r_ = factor(s, r.p+1);
    r.value += (s[r.p] == '+' ? r_.value : - r_.value);
    if (r.value < 0) { throw NegIntExc; }
    r.p = r_.p;
  }
  return r;
}

result factor(const char* &s, int p) {
  result r = term(s, p);
  while (s[r.p] == '*' or s[r.p] == '/') {
    result r_ = term(s, r.p+1);
    if (s[r.p] == '*') {
      r.value *= r_.value;
    } else {
      if (r_.value == 0 or r.value % r_.value != 0) { 
        throw OutOfIntExc; 
      }
      r.value /= r_.value;
    }
    r.p = r_.p;
  }
  return r;
}

result term(const char* &s, int p) {
  if (not isdigit(s[p])) { throw LackNumExc; }
  int value = 0;
  bool flag = false;

  while (isdigit(s[p])) {
    if (flag and value == 0) { throw NotNumExc; }
    flag = true;
    value = value * 10 + (s[p++] - '0');
  }
  return result(value, p);
}

#define REP(i,n) for(int i=0;i<(int)(n);i++)
int H, W, n;
const int HMAX = 200, WMAX = 200, NMAX = 200;
char TABLE[HMAX][WMAX];
bool PRINTED[HMAX][WMAX], HBEGIN[HMAX][WMAX], VBEGIN[HMAX][WMAX];
char BLOCK[NMAX];

void init() {
  REP(y, H) REP(x, W) {
    PRINTED[x][y] = false;
    HBEGIN[x][y] = false;
    VBEGIN[x][y] = false;
  }
}

bool legal(int x, int y) {
  return 0 <= x and x < W and 0 <= y and y < H;
}

void fill_blank() {
  int i = 0;
  REP(y, H) REP(x, W) {
    if (i == n) return;
    if (not PRINTED[x][y]) {
      TABLE[x][y] = BLOCK[i++];
    }
  }
}

string getv(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    y++; 
  }
  return str;
}

string geth(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    x++; 
  }
  return str;
}

bool is_operand(char c) {
  return c == '=' or c == '+' or c == '-' or c == '*' or c == '/';
}

bool is_illegal_operand() {
  REP(y, H) REP(x, W) {
    if (PRINTED[x][y]) continue;
    if (not is_operand(TABLE[x][y])) continue;
    const int dx[4] = {0, 1, 0, -1}, dy[4] = {-1, 0, 1, 0};
    REP(i, 4) {
      int nx = x + dx[i], ny = y + dy[i];
      if (legal(nx, ny) and is_operand(TABLE[nx][ny])) {
        return true;
      }
    }
  }

  REP(y, H) REP(x,W) {
    if ((VBEGIN[x][y] or HBEGIN[x][y]) and is_operand(TABLE[x][y])) {
      return true;
    }
  }
  return false;
}

bool solved() {
  REP(y, H) REP(x, W) {
    if (not HBEGIN[x][y]) continue;
    string eq = geth(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) { return false; }
    } catch (int ex) {
      return false;
    }
  }

  REP(y, H) REP(x, W) {
    if (not VBEGIN[x][y]) continue;
    string eq = getv(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) { return false; }
    } catch(int ex) {
      return false;
    }
  }
  return true;
}

bool solve() {
  sort(BLOCK, BLOCK + n);
  do {
    fill_blank();
/*    REP(y, H) {
      REP(x, W) {
        cout << TABLE[x][y];
      }
      cout << endl;
    }*/
    if (is_illegal_operand()) continue;
    if (solved()) { return true; }
  } while (next_permutation(BLOCK, BLOCK + n));
}

int main() {
  while (cin >> H >> W and H + W) {
    init();

    REP(y, H) {
      string row;
      cin >> row;
      REP(x, W) {
        TABLE[x][y] = row[x];
        PRINTED[x][y] = row[x] != '.';
      }
    }

    REP(y, H) REP(x, W) {
      if (TABLE[x][y]!='#' and legal(x+1,y) and TABLE[x+1][y]!='#'
          and (!legal(x-1,y) or TABLE[x-1][y] == '#')) {
        HBEGIN[x][y] = true;
      }
      if (TABLE[x][y]!='#' and legal(x,y+1) and TABLE[x][y+1]!='#'
          and (!legal(x,y-1) or TABLE[x][y-1] == '#')) {
        VBEGIN[x][y] = true;
      }
    }

    cin >> n;
    REP(i, n) { cin >> BLOCK[i]; }

    cout << (solve() ? "Yes" : "No") << endl;
  }
}

#include <algorithm>
#include <array>
#include <cctype>
#include <cstdlib>
#include <iostream>
#include <utility>
#include <vector>
#include <unordered_set>
using namespace std;

constexpr int MAX_SIZE = 10;
constexpr int MAX_N = 10;

int h, w;
array<string, MAX_SIZE> puzzle;

int n;
array<char, MAX_N> in;

array<bool, 1 << MAX_N> NG;
array<char, 128> letters;
array<bool, 128> is_operator;
array<bool, MAX_N> not_operator;
array<bool, MAX_N> not_zero;
array<bool, MAX_N> not_minus;
array<unordered_set<int>, MAX_N> left_idx, right_idx;

int number(string::const_iterator &it) {
	int res = 0;
	while(isdigit(*it)) {
		res = res * 10 + (*it - '0');
		++it;
	}
	return res;
}

int factor(string::const_iterator &it) {
	if(isdigit(*it)) return number(it);
	throw "parse error";
}

int term(string::const_iterator &it) {
	int res = factor(it);
	while(true) {
		int tmp;
		switch(*it) {
		case '*':
			++it;
			res *= factor(it);
			break;
		case '/':
			++it;
			tmp = factor(it);
			if(tmp == 0) throw "division by zero";
			if(res % tmp) throw "division leaving a remainder";
			res /= tmp;
			break;
		default:
			return res;
		}
		++it;
	}
	return res;
}

int expr(string::const_iterator &it) {
	int res = term(it);
	while(true) {
		switch(*it) {
		case '+':
			++it;
			res += term(it);
			break;
		case '-':
			++it;
			res -= term(it);
			break;
		default:
			return res;
		}
	}
}

bool valid(const vector<int> &expression) {
	const int n = expression.size();
	string left = "", right = "";
	int i;

	for(i = 0; i < n; ++i) {
		if(expression[i] == '=') break;
		left += letters[expression[i]];
	}

	for(++i; i < n; ++i) {
		right += letters[expression[i]];
	}

	try {
		string::const_iterator l_it = left.cbegin(), r_it = right.cbegin();
		if(expr(l_it) != expr(r_it)) return false;
	}
	catch(const char *c) {
		return false;
	}
	return true;
}

bool calc(int &x, int &y, int dx, int dy) {
	while(0 <= x && x < w && 0 <= y && y < h) {
		if(puzzle[y][x] == '=') return false;
		if(puzzle[y][x] == '#') {
			x -= dx;
			y -= dy;
			return true;
		}
		x += dx;
		y += dy;
	}
	x -= dx;
	y -= dy;
	return true;
}

void update(const vector<int> &expression) {
	for(unsigned i = 0; i < expression.size(); ++i) {
		if(isdigit(expression[i])) {
			if(i && expression[i - 1] < MAX_SIZE) {
				not_minus[expression[i - 1]] = true;
				not_zero[expression[i - 1]] = true;
			}
		}
		else if(expression[i] < MAX_SIZE) { // space
			if(i) {
				if(expression[i - 1] < MAX_SIZE) {
					left_idx[expression[i]].insert(expression[i - 1]);
					right_idx[expression[i - 1]].insert(expression[i]);
				}
			}
			else {
				not_minus[expression[i]] = true;
			}
		}
		else { // operator
			if(expression[i - 1] < MAX_SIZE) {
				not_operator[expression[i - 1]] = true;
			}
			if(i + 1 < expression.size() && expression[i + 1] < MAX_SIZE) {
				not_operator[expression[i + 1]] = true;
			}
		}
	}
}

vector<pair<int, vector<int>>> construct() {
	vector<vector<int>> idx(h, vector<int>(w, -1));
	int next = 0;

	vector<pair<int, vector<int>>> expressions;
	expressions.reserve(40);
	for(int i = 0; i < h; ++i) {
		for(int j = 0; j < w; ++j) {
			if(puzzle[i][j] == '=') {
				// vertical
				int s = i - 1, g = i + 1;
				if(calc(j, s, 0, -1) && calc(j, g, 0, 1) && s != i && g != i) {
					vector<int> expression;
					expression.reserve(g - s + 1);
					int max_index = -1;
					for(int k = s; k <= g; ++k) {
						if(puzzle[k][j] == '.') {
							if(idx[k][j] == -1) idx[k][j] = next++;
							expression.emplace_back(idx[k][j]);
							max_index = max(max_index, idx[k][j]);
						}
						else {
							expression.emplace_back(puzzle[k][j]);
						}
					}

					update(expression);
					expressions.emplace_back(max_index, expression);
				}

				// horizontal
				s = j - 1; g = j + 1;
				if(calc(s, i, -1, 0) && calc(g, i, 1, 0) && s != j && g != j) {
					vector<int> expression;
					expression.reserve(g - s + 1);
					int max_index = -1;
					for(int k = s; k <= g; ++k) {
						if(puzzle[i][k] == '.') {
							if(idx[i][k] == -1) idx[i][k] = next++;
							expression.emplace_back(idx[i][k]);
							max_index = max(max_index, idx[i][k]);
						}
						else {
							expression.emplace_back(puzzle[i][k]);
						}
					}

					update(expression);
					expressions.emplace_back(max_index, expression);
				}
			}
		}
	}

	sort(expressions.begin(), expressions.end());
	return expressions;
}

bool dfs(const vector<pair<int, vector<int>>> &expressions, int next, int idx, int used) {
	if(NG[used]) return false;

	while(next < expressions.size() && expressions[next].first < idx) {
		if(!valid(expressions[next].second)) return false;
		++next;
	}
	if(next == expressions.size()) return true;

	for(int i = 0; i < n; ++i) {
		if(used & (1 << i)) continue;
		const char c = in[i];
		if(is_operator[c] && not_operator[idx]) continue;
		if(c == '0' && not_zero[idx]) continue;
		if(c == '-' && not_minus[idx]) continue;

		letters[idx] = c;
		const auto t_no = not_operator;
		const auto t_nz = not_zero;

		if(is_operator[c]) {
			for(const auto &e : left_idx[idx]) not_operator[e] = true;
			for(const auto &e : right_idx[idx]) not_operator[e] = true;
		}
		else {
			for(const auto &e : left_idx[idx]) not_zero[e];
		}

		if(dfs(expressions, next, idx + 1, used | (1 << i))) return true;
		not_operator = t_no;
		not_zero = t_nz;
	}

	NG[used] = true;
	return false;
}

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	is_operator.fill(false);
	is_operator['+'] = true;
	is_operator['-'] = true;
	is_operator['*'] = true;
	is_operator['/'] = true;
	iota(begin(letters), end(letters), 0);

	while(cin >> h >> w && h) {
		not_operator.fill(false);
		not_zero.fill(false);
		not_minus.fill(false);
		for(int i = 0; i < n; ++i) {
			left_idx[i].clear();
			right_idx[i].clear();
		}

		for(int i = 0; i < h; ++i) {
			cin >> puzzle[i];
		}

		cin >> n;
		for(int i = 0; i < n; ++i) {
			cin >> in[i];
		}
		random_shuffle(in.begin(), in.begin() + n);

		const auto expressions = construct();
		NG.fill(false);
		cout << (dfs(expressions, 0, 0, 0) ? "Yes" : "No") << endl;
	}

	return EXIT_SUCCESS;
}

