            O.push_back( a[i] );
        }
    }
    string tmp = "+-*/";
    for ( int i = 0; i < 4; ++ i ) {
        replace( a.begin(), a.end(), tmp[i], ' ' );
    }
    for ( int i = 0; i + 1 < n; ++ i ) {
        if ( a[i] == ' ' && a[i+1] == '0' ) {
            f = false;
            return 0;
        }
    }
    istringstream is( a );
    int d;
    while ( is >> d ) D.push_back( d );
    int m = D.size();

    // *, /
    for ( int i = 0; i + 1 < m; ++ i ) {
        if ( O[i] == '*' ) {
            D[i] = D[i] * D[i + 1];
            D.erase( D.begin() + i + 1 );
            O.erase( O.begin() + i );
            -- i;
            -- m;
        } else if ( O[i] == '/' ) {
            if ( D[i] % D[i+1] != 0 ) {
                f = false;
                return 0;
            }
            D[i] = D[i] / D[i + 1];
            D.erase( D.begin() + i + 1 );
            O.erase( O.begin() + i );
            -- i;
            -- m;
        }
    }

    int answer = D[0];
    for ( int i = 1; i < m; ++ i ) {
        if ( O[i - 1] == '+' ) answer += D[i];
        else answer -= D[i];
    }
    return answer;
}

bool check( string e ) {
    if ( check( e[0] ) ) return false;
    if ( check( e[e.size()-1] ) ) return false;
    for ( int i = 0; i + 1 < (int)e.size(); ++ i ) {
        if ( check( e[i] ) && check( e[i+1] ) ) return false;
        if ( e[i] == '=' && check( e[i+1] ) ) return false;
    }

    replace( e.begin(), e.end(), '=', ' ' );
    istringstream is( e );
    string a;
    string b;
    is >> a >> b;
    bool f = true;
    int na = calc( a, f );
    int nb = calc( b, f );
    return f && nb == na;
}

bool check( VS T ) {
    int H = T.size();
    int W = T[0].size();

    for ( int i = 0; i < H; ++ i ) {
        replace( T[i].begin(), T[i].end(), '#', ' ' );
    }

    VS E;
    for ( int i = 0; i < H; ++ i ) {
        istringstream is( T[i] );
        string e;
        while ( is >> e ) {
            if ( e.size() >= 3 ) E.push_back( e );
        }
    }
    for ( int i = 0; i < W; ++ i ) {
        string t( H, ' ' );
        for ( int j = 0; j < H; ++ j ) t[j] = T[j][i];
        istringstream is( t );
        string e;
        while ( is >> e ) {
            if ( e.size() >= 3 ) E.push_back( e );
        }
    }

    for ( VS ::iterator it_i = E.begin(); it_i != E.end(); ++ it_i ) {
        if ( ! check( *it_i ) ) return false;
    }

    return true;
}

int main() {
    int W, H;
    while ( cin >> H >> W ) {
        if ( W == 0 && H == 0 ) break;

        VS L( H );
        for ( int i = 0; i < H; ++ i ) {
            cin >> L[i];
        }

        int n = 0;
        cin >> n;

        VP B;
        for ( int i = 0; i < H; ++ i ) {
            for ( int j = 0; j < W; ++ j ) {
                if ( L[i][j] == '.' ) {
                    B.push_back( PII( i, j ) ); 
                }
            }
        }
        sort( B.begin(), B.end() );

        VC O( n );
        for ( int i = 0; i < n; ++ i ) {
            cin >> O[i];
        }
        sort( O.begin(), O.end() );

        bool flag = false;
        do {
            VS T = L;
            for ( int i = 0; i < n; ++ i ) {
                int r = B[i].first;
                int c = B[i].second;
                T[r][c] = O[i];
            }
            if ( check( T ) ) {
                flag = true;
                break;
            }
        } while ( next_permutation( O.begin(), O.end() ) );

        cout << ( flag ? "Yes" : "No" ) << endl;
    }
    return 0;
}

