        return false; 
      }
    } catch(int ex) {
      BADVX = x, BADVY = y;
      BADVEQ = eq;
      return false;
    }
  }
  return true;
}

int factorial(int x) {
  int sum = 1;
  for (int i = 1; i <= x; i++) {
    sum *= i;
  }
  return sum;
}

bool nextperm() {
