typedef int Int;

Int term(int &i);
Int fact(int &i);

Int exp(int &i) {
  Int p = term(i);
  while(str[i]=='+' || str[i]=='-') {
    char c = str[i++];
    if (c == '+') {
      Int a = term(i);
      p += a;
    }
    else p -= term(i);
  }
  return p;
}

Int term(int &i) {
  Int p = fact(i);
  while(str[i]=='*' || str[i]=='/') {
    if (str[i++] == '*') {
      p *= fact(i);
    } else {
      int r = fact(i);
      if (p % r == 0) p /= r;
      else throw 0;
    }
  }
  return p;
}

Int fact(int &i) {
  Int p = 0;
  if (!(isdigit(str[i]) && str[i]!='0')) throw 0;
  while(isdigit(str[i])) {
    p *= 10;
    p += str[i]-'0';
    i++;
  }
  return p;
}

Int parse(const string &s) {
  REP(i,s.size()) str[i] = s[i];
  str[s.size()] = '\0';
  int id = 0;
  Int res = exp(id);
  if (id != s.size()) throw 0;
  return res;
}

inline bool isoperator(char c) {
  return c=='+'||c=='-'||c=='*'||c=='/'||c=='=';
}

bool check(const string &s, int id) {
  if (s.size() < 2) return 1;
  if ((id == 0 || isoperator(s[id-1])) && !isdigit(s[id])) return 0;
  if ((int)s.find('.') != string::npos) {
    return 1;
  }
  try {
    int pos = s.find('=');
    if (pos == -1) return 0;
    Int a = parse(s.substr(0,pos));
    Int b = parse(s.substr(pos+1));
    if (a == b) return 1;
    else return 0;
  } catch(int a) {
    return 0;
  }
}

