const int UNCAL = -1111111;
const int PLUS  = UNCAL -1;
const int MINUS = PLUS  -1;
int cal( string s ){
  //  cout << "s="<< s << endl;

  string a;
  vector<int> v;
  bool isNum=true;
  int kind=0; // 0:+,- 1:* 2:/
  for( UI i=0;i<s.length();i++ ){
    if( isNum ){
      a="";
      while( i<s.length() && '0'<=s[i] && s[i]<='9' ) a+=s[i++];
      --i;
      //      cout << " a="<< a << endl;

      if( a.length()==0 ) return UNCAL;
      if( a[0]=='0' && a.length()!=1 ) return UNCAL;
      int number = atoi( a.c_str() );

      if( kind==0 ) v.push_back( number );
      else if( kind==1 ){ // *
	v[ v.size()-1 ] *= number;
      }else{ // /
	if( number==0 ) return UNCAL;
	if( v[v.size()-1]%number !=0 ) return UNCAL;
	v[ v.size()-1 ] /= number;
      }
    }else{
      kind = 0;
      if( s[i]=='+' )      v.push_back( PLUS );
      else if( s[i]=='-' ) v.push_back( MINUS );
      else if( s[i]=='*' ) kind=1;
      else if( s[i]=='/' ) kind=2;
      else return UNCAL;
    }
    isNum = !isNum;
  }
  if( v.size()==0 ) return UNCAL;

  int ret = v[0];
  for( UI i=1;i<v.size();i+=2 ){
    if( v[i]==PLUS && i+1<v.size() ) ret += v[i+1];
    else if( v[i]==MINUS && i+1<v.size() ) ret-=v[i+1];
    else return UNCAL;
  }

  return ret;
}

