  if( i==string::npos || i+1==s.length() )
    return false;
  if( (a=cal(s.substr(0,i)))==UNCAL )
    return false;
  b = cal( s.substr( i+1 ) );

  if( DEBUG ){
    if( a==b )
      cout << " checkForm :"<< s 
	   << " " << "a="<<a <<" b="<< b << endl;
  }
  return a==b;
}


