pii number(const string& s,int i)
{
	int x=0;
	if(i>=s.size() || !isdigit(s[i])) throw __func__;
	for(int len=0;i<s.size() && isdigit(s[i]);i++,len++){
		if(len==1 && x==0) throw __func__;
		x=x*10+s[i]-'0';
	}
	return mp(x,i);
}
pii term(const string& s,int i)
{
	int x; tie(x,i)=number(s,i);
	while(i<s.size() && (s[i]=='*' || s[i]=='/')){
		char op=s[i];
		int y; tie(y,i)=number(s,i+1);
		if(op=='*') x*=y;
		if(op=='/'){
			if(y==0 || x%y>0) throw "zero div";
			x/=y;
		}
	}
	return mp(x,i);
}
pii expr(const string& s,int i)
{
	int x; tie(x,i)=term(s,i);
	while(i<s.size()){
		char op=s[i];
		if(op!='+' && op!='-') throw __func__;
		int y; tie(y,i)=term(s,i+1);
		if(op=='+') x+=y;
		if(op=='-') x-=y;
	}
	return mp(x,i);
}
