constexpr int INF=1e9;
constexpr int MOD=1e9+7;
constexpr double EPS=1e-9;

int expr(const string& s)
{
	int x; char op1;
	for(int i=0,j=0,k=0;i<s.size();i++){
		int y=0; char op2;
		for(j=i;j<s.size();j++){
			int z=0;
			for(k=j;k<s.size() && isdigit(s[k]);k++){
				if(j<k && z==0) return INF; // /0\d+/
				z=z*10+s[k]-'0';
			}
			if(j==i) y=z;
			else{
				assert(op2=='*' || op2=='/');
				if(op2=='*') y*=z;
				else if(z && y%z==0) y/=z;
				else return INF;
			}
			j=k;
			if(i==s.size()) break;
			if(s[j]!='*' && s[j]!='/') break;
			op2=s[j];
		}
		if(i==0) x=y;
		else{
			assert(op1=='+' || op1=='-');
			if(op1=='+') x+=y;
			if(op1=='-') x-=y;
		}
		i=j;
		if(i==s.size()) break;
		if(s[i]!='+' && s[i]!='-') return INF;
		op1=s[i];
	}
	return x;
}

bool check(const vs& grid,const vector<pii>& f)
{
	string s;
	for(auto p:f) s+=grid[p.first][p.second];
	if(count(all(s),'=')!=1) return false;
	int eq=s.find('=');
	if(eq==0 || eq==s.size()-1) return false;
	string a=s.substr(0,eq),b=s.substr(eq+1);

	int x=expr(a),y=expr(b);
	if(x==INF || y==INF || x!=y)
		return false;
	return true;
}

