vector<string> op;

string Op(string str,int &i){
	if(str[i]!='+' && str[i]!='-' && str[i]!='*' && str[i]!='/') return "";
	string res = "";
	res += str[i++];
	return i<str.length() ? res : "";
}

string N(string str,int &i){
	string res = "";
	if(!isdigit(str[i])) return res;
	while(i<str.length() && isdigit(str[i])) res += str[i++];
	return res;
}

string N0(string str,int &i){
	if(str[i]=='0'){
		i++;
		return "0";
	}
	return N(str,i);
}

ll Ex(string str,int &i){
	string tmp;
	if(!isdigit(str[i])) return INF;
	tmp = N0(str,i);
	if(tmp == "") return INF;

	val.push_back(atoll(&tmp[0]));

	while(i<str.length() && str[i]!='='){
		tmp = Op(str,i);
		if(tmp == "") return INF;

		if(!op.empty()){
			string ops = op.back();
			if(ops=="*" || ops=="/" || (ops=="+" || ops=="-") && (tmp=="+" || tmp=="-")){
				ll b = val.back(); val.pop_back();
				ll a = val.back(); val.pop_back();
				op.pop_back();
				if(ops == "+") val.push_back(a+b);
				else if(ops == "-") val.push_back(a-b);
				else if(ops == "*") val.push_back(a*b);
				else if(ops == "/"){
					if(b==0 || a%b!=0) return INF;
					val.push_back(a/b);
				}
			}
		}
		op.push_back(tmp);

		tmp = N0(str,i);
		if(tmp == "") return INF;
		val.push_back(atoll(&tmp[0]));
	}

	while(!op.empty()){
		string ops = op.back(); op.pop_back();
		ll b = val.back(); val.pop_back();
		ll a = val.back(); val.pop_back();
		if(ops == "+") val.push_back(a+b);
		else if(ops == "-") val.push_back(a-b);
		else if(ops == "*") val.push_back(a*b);
		else if(ops == "/"){
			if(b==0 || a%b!=0) return INF;
			val.push_back(a/b);
		}
	}

	return val.back();
}

bool Eq(string str){
	val.clear();
	op.clear();

	int i = 0;
	ll left = Ex(str,i);
	if(left == INF) return false;
	if(i>=str.length() || str[i++]!='=') return false;
	if(i>=str.length()) return false;
	ll right = Ex(str,i);
	if(right == INF) return false;

	return left==right;
}

