			for(;i<h && t[i][j]!='#';i++) str += t[i][j]; //

			if(str.length()>=3 && !Eq(str)) return false;
		}
	}

	//
	rep(i,h){
		int j = 0;
		while(j<w){
			for(;j<w && t[i][j]=='#';j++); //
			if(j>=w) break;
			string str = "";
			str += t[i][j++];
			for(;j<w && t[i][j]!='#';j++) str += t[i][j]; //

			if(str.length()>=3 && !Eq(str)) return false;
		}
	}

	return true;
}

bool solve(int idx){
	if(idx == n) return check();

	rep(i,n) if(!used[i]) {
		used[i] = true;
		t[v[idx].second][v[idx].first] = s[i];
		if(solve(idx+1)) return true;
		used[i] = false;
	}
	return false;
}

