Int term(int &i) {
  Int p = fact(i);
  while(str[i]=='*' || str[i]=='/') {
    if (str[i++] == '*') {
      p *= fact(i);
    } else {
      int r = fact(i);
      if (p % r == 0) p /= r;
      else throw 0;
    }
  }
  return p;
}

Int fact(int &i) {
  Int p = 0;
  if (!isdigit(str[i])) throw 0;
  while(isdigit(str[i])) {
    p *= 10;
    p += str[i]-'0';
    i++;
  }
  return p;
}

Int parse() {
