    memset(gy1, -1, sizeof(gy1));

    for(int i = 0; i < h; i++){
      for(int j = 0; j < w; j++){
        if(t[i][j] == '#') continue;

        if(!isWhite(i + 1 * dy[2], j + 1 * dx[2]) &&
            isWhite(i + 1 * dy[0], j + 1 * dx[0]) &&
