using namespace std;
typedef pair<int,int> result;

const int OutOfIntExc = 1,
          NegIntExc = 2,
          NotEquationExc = 3,
          NotNumExc = 4,
          LackNumExc = 5,
          IterExceedExc = 6;

result equation(const string &s, int p = 0);
result expression(const string &s, int p = 0);
result factor(const string &s, int p = 0);
result term(const string &s, int p = 0);

result equation(const string &s, int p) {
//  cout << "? " << s << " ";
  if (s[p] == '=') { throw NotEquationExc; }
  result r = expression(s, p);
  if (s[r.p] != '=') { throw NotEquationExc; }

  r.p++; // skip '=';
  result t = expression(s, r.p);
  t.value = (int)(r.value == t.value);

//  cout << (t.value ? "T" : "F") << endl;

  return t;
}


result expression(const string &s, int p) {
  result r = factor(s, p);
  while (s[r.p] == '+' or s[r.p] == '-') {
    result r_ = factor(s, r.p+1);
    r.value += (s[r.p] == '+' ? r_.value : - r_.value);
    if (r.value < 0) { throw NegIntExc; }
    r.p = r_.p;
  }
  return r;
}

result factor(const string &s, int p) {
  result r = term(s, p);
  while (s[r.p] == '*' or s[r.p] == '/') {
    result r_ = term(s, r.p+1);
    if (s[r.p] == '*') {
      r.value *= r_.value;
    } else {
      if (r_.value == 0 or r.value % r_.value != 0) { 
        throw OutOfIntExc; 
      }
      r.value /= r_.value;
    }
    r.p = r_.p;
  }
  return r;
}

result term(const string &s, int p) {
  if (not isdigit(s[p])) { throw LackNumExc; }
  int value = 0;
  bool flag = false;

  while (isdigit(s[p])) {
    if (flag and value == 0) { throw NotNumExc; }
    flag = true;
    value = value * 10 + (s[p++] - '0');
  }
  return result(value, p);
}

#define REP(i,n) for(int i=0;i<(int)(n);i++)
int H, W, n;
const int HMAX = 200, WMAX = 200, NMAX = 200;
char TABLE[HMAX][WMAX];
bool PRINTED[HMAX][WMAX], HBEGIN[HMAX][WMAX], VBEGIN[HMAX][WMAX];
char BLOCK[NMAX];

void init() {
  REP(y, H) REP(x, W) {
    PRINTED[x][y] = false;
    HBEGIN[x][y] = false;
    VBEGIN[x][y] = false;
  }
}

bool legal(int x, int y) {
  return 0 <= x and x < W and 0 <= y and y < H;
}

void fill_blank() {
  int i = 0;
  REP(y, H) REP(x, W) {
    if (i == n) return;
    if (not PRINTED[x][y]) {
      TABLE[x][y] = BLOCK[i++];
    }
  }
}

string getv(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    y++; 
  }
  return str;
}

string geth(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    x++; 
  }
  return str;
}

bool solved() {
  REP(y, H) REP(x, W) {
    if (not HBEGIN[x][y]) continue;
    string eq = geth(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) { return false; }
    } catch (int ex) {
      return false;
    }
  }

  REP(y, H) REP(x, W) {
    if (not VBEGIN[x][y]) continue;
    string eq = getv(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) { return false; }
    } catch(int ex) {
      return false;
    }
  }
  return true;
}

bool solve() {
  sort(BLOCK, BLOCK + n);
  do {
    fill_blank();
/*    REP(y, H) {
      REP(x, W) {
        cout << TABLE[x][y];
      }
      cout << endl;
    }*/
    if (solved()) { return true; }
  } while (next_permutation(BLOCK, BLOCK + n));
}

int main() {
  while (cin >> H >> W and H + W) {
    init();

    REP(y, H) {
      string row;
      cin >> row;
      REP(x, W) {
        TABLE[x][y] = row[x];
        PRINTED[x][y] = row[x] != '.';
      }
    }

    REP(y, H) REP(x, W) {
      if (TABLE[x][y]!='#' and legal(x+1,y) and TABLE[x+1][y]!='#'
          and (!legal(x-1,y) or TABLE[x-1][y] == '#')) {
        HBEGIN[x][y] = true;
      }
      if (TABLE[x][y]!='#' and legal(x,y+1) and TABLE[x][y+1]!='#'
          and (!legal(x,y-1) or TABLE[x][y-1] == '#')) {
        VBEGIN[x][y] = true;
      }
    }
    cin >> n;
    REP(i, n) { cin >> BLOCK[i]; }

    cout << (solve() ? "Yes" : "No") << endl;
  }
}

#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

bool eval_factor(const string& str, int& p, int& val)
{
  if (p >= str.size()) {
    return false;
  }
  if (str[p] < '0' || '9' < str[p]) {
    return false;
  }
  val = str[p] - '0';
  ++p;
  if (str[p] == '0') {
    val = 0;
    return true;
  }
  while (p < str.size() && '0' <= str[p] && str[p] <= '9') {
    val = 10*val + (str[p] - '0');
    ++p;
  }
  return true;
}

bool eval_term(const string& str, int& p, int& val)
{
  if (p >= str.size()) {
    return false;
  }
  if (!eval_factor(str, p, val)) {
    return false;
  }
  while (p < str.size() && (str[p] == '*' || str[p] == '/')) {
    const char op = str[p];
    ++p;
    int x;
    if (!eval_factor(str, p, x)) {
      return false;
    }
    if (op == '*') {
      val *= x;
    } else {
      val /= x;
    }
  }
  return true;
}

bool eval_expr(const string& expr, int& p, int& val)
{
  if (p >= expr.size()) {
    return false;
  }
  if (!eval_term(expr, p, val)) {
    return false;
  }
  while (p < expr.size() && (expr[p] == '+' || expr[p] == '-')) {
    const char op = expr[p];
    ++p;
    int x;
    if (!eval_term(expr, p, x)) {
      return false;
    }
    if (op == '+') {
      val += x;
    } else {
      val -= x;
    }
  }
  return true;
}

bool check(const string& expr)
{
  if (expr.size() == 1) {
    return true;
  }
 int p = 0;
 int lhs, rhs;
 if (!eval_expr(expr, p, lhs)) {
   return false;
 }
 if (p < expr.size() && expr[p] != '=') {
   return false;
 }
 ++p;
 if (!eval_expr(expr, p, rhs)) {
   return false;
 }
 return p == expr.size() && lhs == rhs;
}//

bool valid(const vector<string>& grid)
{
  const int H = grid.size(), W = grid[0].size();
  for (int i = 0; i < H; i++) {
    for (int j = 0; j < W; j++) {
      if (grid[i][j] != '#') {
        if (j == 0 || grid[i][j-1] == '#') {
          string expr(grid[i].begin()+j, find(grid[i].begin()+j, grid[i].end(), '#'));
          if (!check(expr)) {
            return false;
          }
        }
        if (i == 0 || grid[i-1][j] == '#') {
          string expr;
          for (int k = i; k < H && grid[k][j] != '#'; k++) {
            expr += grid[k][j];
          }
          if (!check(expr)) {
            return false;
          }
        }
      }
    }
  }
  return true;
}

bool solve(vector<string>& grid, const vector<pair<int,int> >& spaces, vector<char>& cards)
{
  sort(cards.begin(), cards.end());
  do {
    for (size_t i = 0; i < cards.size(); i++) {
      grid[spaces[i].first][spaces[i].second] = cards[i];
    }
    if (valid(grid)) {
      return true;
    }
  } while (next_permutation(cards.begin(), cards.end()));
  return false;
}

int main()
{
  int H, W;
  while (cin >> H >> W && H != 0) {
    vector<string> grid(H);
    vector<pair<int,int> > spaces;
    for (int i = 0; i < H; i++) {
      cin >> grid[i];
      for (int j = 0; j < W; j++) {
        if (grid[i][j] == '.') {
          spaces.push_back(make_pair(i, j));
        }
      }
    }
    int N;
    cin >> N;
    vector<char> cards(N);
    for (int i = 0; i < N; i++) {
      cin >> cards[i];
    }
    if (solve(grid, spaces, cards)) {
      cout << "Yes" << endl;
    } else {
      cout << "No" << endl;
    }
  }
  return 0;
}

#include <algorithm>
#include <array>
