// ?????????????????????????????????????????????????????????????????????
int term(State &begin) {
    if(end_parse) return -1;
    int ret = factor(begin);

    for (;;) {
        if (*begin == '*') {
            begin++;
            ret *= factor(begin);
        } else if (*begin == '/') {
            begin++;
            int tmp = factor(begin);
            if(tmp == 0 || ret % tmp != 0) {
                end_parse = 1;
                return 1e7;
            }
            else ret /= tmp;
        } else {
            break;
        }
    }

    return ret;
}

// ?????????????????????????????????????????????????????????????????????
int expression(State &begin) {
    if(end_parse) return -1;
    int ret = term(begin);

    for (;;) {
        if (*begin == '+') {
            begin++;
            ret += term(begin);
        } else if (*begin == '-') {
            begin++;
            ret -= term(begin);
            if(ret < 0){
                end_parse = true;
                return 1e7;
            }
        } else {
            break;
        }
    }

    return ret;
}

bool eq(State &begin){
    int ret = expression(begin);
    if(end_parse) return 0;

    if(*begin == '='){
        begin++;
        return (ret == expression(begin));
    }
    return 0;
}

