#define value first
#define p second
using namespace std;
typedef pair<int,int> result;

const int OutOfIntExc = 1,
          NegIntExc = 2,
          NotEquationExc = 3,
          NotNumExc = 4,
          LackNumExc = 5,
          IterExceedExc = 6;

result equation(const string &s, int p = 0);
result expression(const string &s, int p = 0);
result factor(const string &s, int p = 0);
result term(const string &s, int p = 0);

result equation(const string &s, int p) {
//  cout << "? " << s << " ";
  if (s[p] == '=') { throw NotEquationExc; }
  result r = expression(s, p);
  if (s[r.p] != '=') { throw NotEquationExc; }

  r.p++; // skip '=';
  result t = expression(s, r.p);
  t.value = (int)(r.value == t.value);

//  cout << (t.value ? "T" : "F") << endl;

  return t;
}


result expression(const string &s, int p) {
  result r = factor(s, p);
  while (s[r.p] == '+' or s[r.p] == '-') {
    result r_ = factor(s, r.p+1);
    r.value += (s[r.p] == '+' ? r_.value : - r_.value);
    if (r.value < 0) { throw NegIntExc; }
    r.p = r_.p;
  }
  return r;
}

result factor(const string &s, int p) {
  result r = term(s, p);
  while (s[r.p] == '*' or s[r.p] == '/') {
    result r_ = term(s, r.p+1);
    if (s[r.p] == '*') {
      r.value *= r_.value;
    } else {
      if (r_.value == 0 or r.value % r_.value != 0) { 
        throw OutOfIntExc; 
      }
      r.value /= r_.value;
    }
    r.p = r_.p;
  }
  return r;
}

result term(const string &s, int p) {
  if (not isdigit(s[p])) { throw LackNumExc; }
  int value = 0;
  bool flag = false;

  while (isdigit(s[p])) {
    if (flag and value == 0) { throw NotNumExc; }
    flag = true;
    value = value * 10 + (s[p++] - '0');
  }
  return result(value, p);
}

#define REP(i,n) for(int i=0;i<(int)(n);i++)
int H, W, n;
const int HMAX = 200, WMAX = 200, NMAX = 200;
char TABLE[HMAX][WMAX];
bool PRINTED[HMAX][WMAX], HBEGIN[HMAX][WMAX], VBEGIN[HMAX][WMAX];
char BLOCK[NMAX];

bool legal(int x, int y) {
  return 0 <= x and x < W and 0 <= y and y < H;
}

void fill_blank() {
  int i = 0;
  REP(y, H) REP(x, W) {
    if (i == n) return;
    if (not PRINTED[x][y]) {
      TABLE[x][y] = BLOCK[i++];
    }
  }
}

string getv(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    y++; 
  }
  return str;
}

string geth(int x, int y) {
  string str;
  while (legal(x, y) and TABLE[x][y] != '#') { 
    str.push_back(TABLE[x][y]);
    x++; 
  }
  return str;
}

bool is_operand(char c) {
  return c == '=' or c == '+' or c == '-' or c == '*' or c == '/';
}

bool is_illegal_operand() {
  REP(y, H) REP(x, W) {
    if (PRINTED[x][y]) continue;
    if (not is_operand(TABLE[x][y])) continue;
    const int dx[4] = {0, 1, 0, -1}, dy[4] = {-1, 0, 1, 0};
    REP(i, 4) {
      int nx = x + dx[i], ny = y + dy[i];
      if (legal(nx, ny) and is_operand(TABLE[nx][ny])) {
        return true;
      }
    }
  }

  REP(y, H) REP(x,W) {
    if ((VBEGIN[x][y] or HBEGIN[x][y]) and is_operand(TABLE[x][y])) {
      return true;
    }
  }
  return false;
}

int hbadx, hbady, vbadx, vbady;
bool hbadflag, vbadflag;
string hbadeq, vbadeq;

bool solved() {
  if (hbadflag and geth(hbadx, hbady) == hbadeq) return false;
  if (vbadflag and geth(vbadx, vbady) == vbadeq) return false;
  REP(y, H) REP(x, W) {
    if (not HBEGIN[x][y]) continue;
    string eq = geth(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) {
        hbadflag = true;
        hbadx = x, hbady = y;
        hbadeq = eq;
        return false; 
      }
    } catch (int ex) {
      hbadflag = true;
      hbadx = x, hbady = y;
      hbadeq = eq;
      return false;
    }
  }

  REP(y, H) REP(x, W) {
    if (not VBEGIN[x][y]) continue;
    string eq = getv(x, y);
    try {
      result e = equation(eq);
      if (e.value == 0) {
        vbadflag = true;
        vbadx = x, vbady = y;
        vbadeq = eq;
        return false; 
      }
    } catch(int ex) {
      vbadflag = true;
      vbadx = x, vbady = y;
      vbadeq = eq;
      return false;
    }
  }
  return true;
}

bool solve() {
  sort(BLOCK, BLOCK + n);
  do {
    fill_blank();
    if (is_illegal_operand()) continue;
    if (solved()) { return true; }
  } while (next_permutation(BLOCK, BLOCK + n));
}

void init() {
  REP(y, H) REP(x, W) {
    hbadflag = vbadflag = false;
    PRINTED[x][y] = false;
    HBEGIN[x][y] = false;
    VBEGIN[x][y] = false;
  }
}

int main() {
  while (cin >> H >> W and H + W) {
    init();

    REP(y, H) {
      string row;
      cin >> row;
      REP(x, W) {
        TABLE[x][y] = row[x];
        PRINTED[x][y] = row[x] != '.';
      }
    }

    REP(y, H) REP(x, W) {
      if (TABLE[x][y]!='#' and legal(x+1,y) and TABLE[x+1][y]!='#'
          and (!legal(x-1,y) or TABLE[x-1][y] == '#')) {
        HBEGIN[x][y] = true;
      }
      if (TABLE[x][y]!='#' and legal(x,y+1) and TABLE[x][y+1]!='#'
          and (!legal(x,y-1) or TABLE[x][y-1] == '#')) {
        VBEGIN[x][y] = true;
      }
    }

    cin >> n;
    REP(i, n) { cin >> BLOCK[i]; }

    cout << (solve() ? "Yes" : "No") << endl;
  }
}

#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=(a);i<(b);++i)

const int dx[4] = {-1,0,1,0};
const int dy[4] = {0,-1,0,1};

class Equation {
public:
	string A, B;
	Equation(string S) {
		int p = S.find("=");
		A = S.substr(0, p);
		B = S.substr(p+1);
		// cerr << A << " " << B << endl;
	}

	string convert(string S, string symbol_map) {
		for (char& c : S) if ('a' <= c && c <= 'z') c = symbol_map[c - 'a'];
		return S;
	}

	pair< int, bool > calculate(string S) {
		stack< char > stack_op;
		stack< int > stack_num;

		auto calc = [&]() {
			char op = stack_op.top(); stack_op.pop();
			int b = stack_num.top(); stack_num.pop();
			int a = stack_num.top(); stack_num.pop();
			//cerr << a << op << b << endl;
			if (op == '+') stack_num.push(a + b);
			if (op == '-') stack_num.push(a - b);
			if (op == '*') stack_num.push(a * b);
			if (op == '/') {
				if (a % b != 0 || b == 0) return false;
				stack_num.push(a / b);
			}
			return true;	
		};

		S += '#';
		int num = -1;
		for (char c : S) {
			if (isdigit(c)) {
				if (num == -1) num = 0;
				num = 10 * num + (c - '0');
			}
			else {
				if (num != -1) {
					stack_num.push(num);
					num = -1;
					while (stack_op.top() == '*' || stack_op.top() == '/') {
						if (!calc()) return pair< int, bool >(0, false);
					}
				}
				if (c != '#') stack_op.push(c);
			}
		}

		while (!stack_op.empty()) calc();
		return pair< int, bool >(stack_num.top(), true);
	}

	bool valid(string S) {
		int len = S.size();
		bool num_flag = false, op_flag = false;
		for_(i,0,len) {
			char c = S[i];
			if (isdigit(c)) {
				if (!num_flag && c == '0' && i<len-1 && isdigit(S[i+1])) return false; // reading zero
				num_flag = true;
				op_flag = false;
			}
			else {
				if (!num_flag || op_flag) return false; // unary operation
				num_flag = false;
				op_flag = true;
			}
		}
		return num_flag;
	}

	bool evaluate(string symbol_map) {
		string X = convert(A, symbol_map);
		string Y = convert(B, symbol_map);
		if (!valid(X) || !valid(Y)) return false;
		pair< int, bool > pX = calculate(X), pY = calculate(Y);
		//cerr << X << " ? " << Y << " -> " << pX.first << " ? " << pY.first << endl;
		return  pX.second && pY.second && pX.first == pY.first;
	}
};

int H, W, n, buf[11][11];
string board[11];
char symbol[11];

vector< Equation > findEquations() {
	vector< Equation > ret;

	for_(d,0,2) {
		memset(buf, 0, sizeof(buf));
		for_(y,0,H) for_(x,0,W) {
			if (board[y][x] != '#' && buf[y][x] == 0) {
				string S = "";
				int cx = x, cy = y;
				while (cy < H && cx < W && board[cy][cx] != '#') {
					S += board[cy][cx];
					buf[cy][cx] = 1;
					if (d == 0) ++cy;
					else ++cx;
				}
				if (S.size() >= 3) {
					ret.push_back(Equation(S));
					//cerr << S << endl;
				}
			}
		}
	}

	return ret;
}

void solve() {
	int a = 0;
	for_(y,0,H) {
		cin >> board[y];
		for_(x,0,W) {
			if (board[y][x] == '.') {
				board[y][x] = char('a' + a);
				++a;
			}
		}
	}

	cin >> n;
	for_(i,0,n) cin >> symbol[i];
	sort(symbol, symbol + n);

	vector< Equation > eqs = findEquations();

	do {
		string s_map = "";
		for_(i,0,n) s_map += symbol[i];
		//cerr << s_map << endl;
		bool flag = true;
		for (Equation eq : eqs) flag &= eq.evaluate(s_map);
		if (flag) {
			puts("Yes");
			return;
		}
	} while (next_permutation(symbol, symbol + n));

	puts("No");
}

int main() {
	while (cin >> H >> W, H) {
		solve();
	}
}

