bool inBoard(int x, int y){
  return 0 <= x && x < w && 0 <= y && y < h;
}

//#true
bool isWhite(int x, int y){
  if(!inBoard(x, y)) return false;
  return t[y][x] != '#';
}

//true
bool containsOperator(int x, int y){
  if(!inBoard(x, y)) return false;
  return isOperator[t[y][x]];
}

