Int term(int &i) {
  Int p = fact(i);
  while(str[i]=='*' || str[i]=='/') {
    if (str[i++] == '*') {
      p *= fact(i);
    } else {
      int r = fact(i);
      if (p % r == 0) p /= r;
      else throw 0;
    }
  }
  return p;
}

