typedef int Int;

Int term(int &i);
Int fact(int &i);

Int exp(int &i) {
  Int p = term(i);
  while(str[i]=='+' || str[i]=='-') {
    char c = str[i++];
    if (c == '+') {
      Int a = term(i);
      p += a;
    }
    else p -= term(i);
  }
  return p;
}

Int term(int &i) {
  Int p = fact(i);
  while(str[i]=='*' || str[i]=='/') {
    if (str[i++] == '*') {
      p *= fact(i);
    } else {
      int r = fact(i);
      if (r == 0) throw 0;
      if (p % r == 0) p /= r;
      else throw 0;
    }
  }
  return p;
}

Int fact(int &i) {
  Int p = 0;
  if (!isdigit(str[i])) throw 0;
  while(isdigit(str[i])) {
    p *= 10;
    p += str[i]-'0';
    i++;
  }
  return p;
}

Int parse() {
  int id = 0;
  Int res = exp(id);
  if (str[id]) throw 0;
  return res;
}

inline bool isoperator(char c) {
  return c=='+'||c=='-'||c=='*'||c=='/'||c=='=';
}

bool check(const string &s, int id, bool f=1) {
  if (s.size() < 2) return 1;
  // if (f) {
  //   if ((id == 0 || isoperator(s[id-1])) && (!isdigit(s[id]) || s[id] == '0')) return 0;
  //   if ((id == s.size()-1 || isoperator(s[id+1])) && !isdigit(s[id])) return 0;
  // }
  REP(i,s.size()-1) {
    if ((i==0 || !isdigit(s[i-1])) && s[i] == '0' && isdigit(s[i+1])) return 0;
    if (isoperator(s[i]) && isoperator(s[i+1])) return 0;
  }
  if ((int)s.find('.') != string::npos) {
    return 1;
  }
  try {
    int pos = s.find('=');
    if (pos == -1 || pos == 0 || pos == s.size()-1) return 0;
    for (int i=0; i<pos; ++i) str[i] = s[i]; str[pos] = 0;
    Int a = parse();
    for (int i=pos+1; i<s.size(); ++i) str[i-pos-1] = s[i]; str[s.size()-pos-1] = 0;
    Int b = parse();
    if (a == b) return 1;
    else return 0;
  } catch(int a) {
    return 0;
  }
}

