bool isop(char c) {
    return c == '+' || c == '-' || c == '*' || c == '/';
}
bool calc(int &x1, int x2, char op) {
    if (op == '+') x1 += x2;
    else if (op == '-') x1 -= x2;
    else if (op == '*') x1 *= x2;
    else if (op == '/') {
        if (x2 == 0 || x1 % x2) return false;
        x1 /= x2;
    }
    return true;
}
bool calc_exp(char *s, int n, int &val) {
    if (n == 0) return false;
    int now = 0;
    vector<int> num;
    vector<char> op;
    for (int i = 0; i <= n; ++i) {
        if (i == n || s[i] == '+' || s[i] == '-') {
            num.push_back(now);
            now = 0;
            while (num.size() > 1) {
                int x2 = num.back();
                num.pop_back();
                int x1 = num.back();
                num.pop_back();
                if (!calc(x1, x2, op.back())) return false;
                op.pop_back();
                num.push_back(x1);
            }
            if (i != n) op.push_back(s[i]);
        } else if (isdigit(s[i])) {
            now = now * 10 + s[i] - '0';
        } else if (s[i] == '*' || s[i] == '/') {
            num.push_back(now);
            now = 0;
            while (op.size() > 0 && (op.back() == '*' || op.back() == '/')) {
                int x2 = num.back();
                num.pop_back();
                int x1 = num.back();
                num.pop_back();
                if (!calc(x1, x2, op.back())) return false;
                op.pop_back();
                num.push_back(x1);
            }
            op.push_back(s[i]);
        }
    }
    val = num[0];
    return true;
}
bool legal_exp(char *s, int n, int &val) {
    for (int i = 0; i < n; ++i) {
        if (i + 1 < n && s[i] == '0' && isdigit(s[i + 1])) return false;
        //if ((i == 0 || i == n - 1) && isop(s[i])) return false;
        //if (i + 1 < n && isop(s[i]) && isop(s[i + 1])) return false;
    }
    if (!calc_exp(s, n, val)) return false;
    return true;
}
bool legal_equility(char *s, int n) {
    if (n <= 2) return true;
    int x = -1;
    for (int i = 0; i < n; ++i) {
        if (s[i] == '=') {
            if (x >= 0) return false;
            x = i;
        }
    }
    if (x == -1) return true;
    for (int i = 0; i < n; ++i) {
        if ((i == 0 || i == n - 1) && isop(s[i])) return false;
        if (i + 1 < n && isop(s[i]) && isop(s[i + 1])) return false;
        if (i + 1 < n && isop(s[i]) && s[i + 1] == '=') return false;
        if (i - 1 >= 0 && isop(s[i]) && s[i - 1] == '=') return false;
    }
    for (int i = 0; i < n; ++i) {
        if (s[i] == '.') return true;
    }
    int x1, x2;
    if (!legal_exp(s, x, x1) || !legal_exp(s + x + 1, n - x - 1, x2)) return false;
    return x1 == x2;
}
bool check_ori() {
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (s[i][j] == '#') continue;
            int k = j + 1;
            while (k < m && s[i][k] != '#') ++k;
            if (!legal_equility(s[i] + j, k - j)) return false;
            j = k;
        }
    }
    char str[maxn];
    int len;
    for (int j = 0; j < m; ++j) {
        for (int i = 0; i < n; ++i) {
            if (s[i][j] == '#') continue;
            len = 0;
            while (i + len < n && s[i + len][j] != '#') {
                str[len] = s[i + len][j];
                ++len;
            }
            if (!legal_equility(str, len)) return false;
            i += len;
        }
    }
    return true;
}

bool ok(int x1, int x2) {
    int lf = x2, rt = x2;
    while (lf >= 0 && s[x1][lf] != '#') --lf;
    while (rt < m && s[x1][rt] != '#') ++rt;
    ++lf;
    --rt;
    if (!legal_equility(s[x1] + lf, rt - lf + 1)) return false;
    char str[maxn];
    int len = 0;
    while (x1 >= 0 && s[x1][x2] != '#') --x1;
    ++x1;
    while (x1 < n && s[x1][x2] != '#') {
        str[len++] = s[x1++][x2];
    }
    if (!legal_equility(str, len)) return false;
    return true;
}

bool dfs(int dep) {
    if (dep == cnt) {
        return true;
    }
    for (int i = 0; i < k; ++i) {
        if (used[i]) continue;
        if (i > 0 && d[i] == d[i - 1] && !used[i - 1]) continue;
        s[rec[dep].first][rec[dep].second] = d[i];
        used[i] = true;
        if (ok(rec[dep].first, rec[dep].second) && dfs(dep + 1)) return true;
        used[i] = false;
        s[rec[dep].first][rec[dep].second] = '.';
    }
    return false;
}

bool check() {
    if (cnt > k) return false;
    if (!check_ori()) return false;
    memset(used, 0, sizeof(used));
    if (!dfs(0)) return false;
    return true;
}
bool cmp(char x1, char x2) {
    bool s1 = isop(x1), s2 = isop(x2);
    if (s1 != s2) return s1 > s2;
    return x1 < x2;
}
