bool flag;

const int dx[] = {1,0,-1,0};
const int dy[] = {0,1,0,-1};

int getNum(string &s, int &pos) {
  string num = "";
  while(pos < s.size() && isdigit(s[pos])) {
    num += s[pos++];
  }
  if(num[0] == '0') flag = true;
  return atoi(num.c_str());
}


int term(string &s, int &pos) {
  int x = getNum(s, pos);
  while(pos < s.size()) {
    if(s[pos] == '*') {
      ++pos;
      x *= getNum(s, pos);
    } else if(s[pos] == '/') {
      ++pos;
      int tmp = getNum(s, pos);
      if(tmp == 0 || x % tmp != 0) {
	flag = true;
	return -1;
      }
      x /= tmp;
    } else {
      break;
    }
  }
  return x;
}

int exp(string &s, int &pos) {
  int x = term(s, pos);
  while(pos < s.size()) {
    if(s[pos] == '+') {
      ++pos;
      x += term(s, pos);
    } else if(s[pos] == '-') {
      ++pos;
      x -= term(s, pos);
    }else {
      break;
    }
  }
  return x;
}

bool check(int x, int y, int dir) {
  string s[2];
  s[0] = s[1] = "";
  int p = 0;
  int a, b;
  do {
    if(M[y][x] == '=') {
      ++p;
    } else {
      s[p] += M[y][x];
    }
    x += dx[dir];
    y += dy[dir];
  } while(x < W && y < H);
  for(int i = 0; i < 2; ++i) 
    if(!isdigit(s[i][0]) || !isdigit(s[i][(int)s[i].size()-1])) return false;
  int pos;
  flag = false;

  pos = 0;
  a = exp(s[0], pos);
  pos = 0;
  b = exp(s[1], pos);

  return !flag && a == b;
}

bool judge() {
  for(int i = 0; i < H; ++i) {
    for(int j = 0; j < W; ++j) {
      if((i == 0 || M[i-1][j] == '#') &&
	 M[i][j] != '#' && i+2 < H && M[i+1][j] != '#' && M[i+2][j] != '#') {
	if(!check(j, i, 1)) return false;
      }
      if((j == 0 || M[i][j-1] == '#') &&
	 M[i][j] != '#' && j+2 < W && M[i][j+1] != '#' && M[i][j+2] != '#') {
	if(!check(j, i, 0)) return false;
      }
    }
  }
  return true;
}

bool rec(int t) {
  if(t == N) {
    return judge();
  }

  for(int i = 0; i < N; ++i) {
    int x = V[i].first;
    int y = V[i].second;
    if(M[y][x] != '.') continue;
    int type = -1; // 0:Num, 1:Operator
    for(int i = 0; i < 4; ++i) {
      int nx = x + dx[i];
      int ny = y + dy[i];
      if(nx < 0 || nx >= W) continue;
      if(ny < 0 || ny >= H) continue;
      if(M[ny][nx] != '#') {
	if(isdigit(M[ny][nx])) type = 0;
	else type = 1; 
	break;
      }
    }
    if(!isdigit(T[t]) && type == 1) continue;

    M[y][x] = T[t];
    if(rec(t+1)) return true;
    M[y][x] = '.';
  }
  return false;
}

int main() {
