int expr(const string& s)
{
	int x; char op1;
	for(int i=0,j=0,k=0;i<s.size();i++){
		int y=0; char op2;
		for(j=i;j<s.size();j++){
			if(s[j]<'1' || '9'<s[j]) return INF;
			int z=0;
			for(k=j;k<s.size() && isdigit(s[k]);k++)
				z=z*10+s[k]-'0';
			if(j==i) y=z;
			else if(op2=='*') y*=z;
			else if(z && y%z==0) y/=z;
			else return INF;
			j=k;
			if(i==s.size()) break;
			if(s[j]!='*' && s[j]!='/') break;
			op2=s[j];
		}
		if(i==0) x=y;
		else op1=='+'?x+=y:x-=y;
		i=j;
		if(i==s.size()) break;
		if(s[i]!='+' && s[i]!='-') return INF;
		op1=s[i];
	}
	return x;
}

