		int val = 0;
		if (exp[p] == '+' ){
			++p;
			val = Ex();
			if (val == UND ) ans = false;
			value1 += val;
		}else{
			++p;
			val = Ex();
			if (val == UND ) ans = false;
			value1 -= val;		
		} // end if
	} // end while

	if (exp[p] == '=' ){
		++p;
	} // end if

	value2 = Ex();			

	if (value2 == UND ) ans = false;

	while (exp[p] == '+' || exp[p] == '-' ){
		int val = 0;
		if (exp[p] == '+' ){
			++p;
			val = Ex();
			if (val == UND ) ans = false;
			value2 += val;
		}else{
			++p;
			val = Ex();
			if (val == UND ) ans = false;
			value2 -= val;		
		} // end if
	} // end while

	if (value1 != value2 )
		ans = false;
#if DEBUG
	cout << "ans: " << (ans ? "YES" : "NO" ) << endl;
#endif
}

int Parser::Ex (void )
{
	int value = N();

	if (value == UND ) ans = false;

	while (exp[p] == '*' || exp[p] == '/' ){
		if (exp[p] == '*' ){
			++p;
			int val = N();
			if (val == UND ){
				value = UND;
				ans = false;
			}else{
				value *= val;
			} // end if
		}else{
			++p;
			int val = N();
			if (value != UND && val != UND && val != 0 && value % val == 0 ){
				value /= val;
			}else{
				value = UND;
				ans = false;
			} // end if
		} // end if
	} // end while

	return value;
}

int Parser::N (void )
{
	int value = 0;
	int cnt = 0;
	char first = 0;
	if (!isdigit(exp[p] ) )
		return UND;

	while (isdigit(exp[p] ) ){
		if (cnt == 0 ){
			first = exp[p];
		} // end if
		value *= 10;
		value += (exp[p] - '0');
		cnt++;
		p++;
	} // end while

	if ( (first == '0' && cnt > 1 ) || (cnt > 2 ) )
		ans = false;

	return value;
}

