bool bfs(int y, int x, int dy, int dx) {
  char s[11];
  int idx = 0;
  vector<pii> v;
  vector<int> id;
  while(valid(y,x,h,w)) {
    visited[y][x][dy] = 1;
    if (a[y][x] == '#') break;
    if (a[y][x] == '.') {
      v.push_back(pii(y,x));
      id.push_back(idx);
    }
    s[idx++] = a[y][x];
    y += dy;
    x += dx;
  }
  if (idx >= 3) {
    s[idx] = 0;
    if (v.size() == 0 && !check(s)) return 0;
    dotNum[eqNum] = v.size();
    strcpy(eq[eqNum], s);
    REP(i,v.size()) {
      int y = v[i].first, x = v[i].second;
      ididx[y][x].push_back(pii(eqNum,id[i]));
      if (!inserted[y][x]) {
        pos[posNum++] = pii(y,x);
        inserted[y][x] = 1;
      }
    }
    eqNum++;
  }
  return 1;
}

bool checkOp(int id, int idx) {
  int n = strlen(eq[id]);
  if (idx==0 || idx==n-1) return 1;
  if (isoperator(eq[id][idx-1])) return 1;
  if (isoperator(eq[id][idx+1])) return 1;
  return 0;
}

