}

bool check(){

  for(int i=0;i<(int)w.size();i++){
    if(compute(i)==false)return false;
  }
  return true;
}

bool compute(int x){
  calcStr a,b;
  a.s=w[x];
  b.s=w2[x];
  for(int i=0;i<(int)a.s.size();i++){
    if('A'<=a.s[i]&&a.s[i]<='Z')a.s[i]=u[a.s[i]-'A'];
  }
  for(int i=0;i<(int)b.s.size();i++){
    if('A'<=b.s[i]&&b.s[i]<='Z')b.s[i]=u[b.s[i]-'A'];
  }
  a.init();
  b.init();
  int A=a.getAns();
  int B=b.getAns();
  if(A==INF||B==INF){
    return false;
  }

  return (A==B);
