bool is_correct (void )
{
	const int dir[][2] = { { 0, 0 }, { 1, 0 }, { 0, 1 } };

	memset (used, false, sizeof (used ) );
	stack<P> u;
	rep (i, h ){
		rep (j, w ){
			if (cur[i][j] != '#' ){
				int k1 = 0, k2 = 0;
				if (!used[i][j] ){
					k1 = 0, k2 = 2;
				}else{ // if (used[i][j] )
					int dsum = 0;
					rep (d, 3 ){
						int ni = i + dir[d][0];
						int nj = j + dir[d][1];
						if (ni < 0 || nj < 0 || ni >= h || nj >= w || cur[ni][nj] == '#' ) continue;
						if (!used[ni][nj] ){
							dsum += d;
						} // end if
					} // end rep
					switch (dsum ){
						case 1: k1 = 0, k2 = 1; break;
						case 2: k1 = 1, k2 = 2; break;
						case 3: k1 = 0, k2 = 2; break;
						default: k1 = 0, k2 = 0; break;
					} // end switch
				} // end if
				for (int k = k1; k < k2; k++ ){
					bool equ = false;
					string exp = "";
					for (int e = (k == 0 ? i : j ); e < (k == 0 ? h : w ); e++ ){
						int row = (k == 0 ? e : i );
						int col = (k == 0 ? j : e );
						if (cur[row][col] != '#' ){
							if (cur[row][col] != '=' ){
								exp += cur[row][col];
								used[row][col] = true;
								u.push (P (row, col ) );
							}else
							if (!equ ){
								exp += cur[row][col];
								used[row][col] = true;
								u.push (P (row, col ) );
								equ = true;
							}else{
								if (!evaluate_exp (exp ) )
									return false;
							} // end if
						}else{ // if (cur[row][col] == '#' )
							if (equ ){
								while (!u.empty() ) u.pop();
								if (!evaluate_exp (exp ) )
									return false;
								equ = false;
							}else{
								while (!u.empty() ){
									P c = u.top(); u.pop();
									used[c.first][c.second] = false;
								} // end while
							} // end if
							exp = "";
							break;
						} // end if
					} // end for
					if (!exp.empty() ){
						if (equ ){
							while (!u.empty() ) u.pop();
							if (!evaluate_exp (exp ) )
								return false;
						}else{
							while (!u.empty() ){
								P c = u.top(); u.pop();
								used[c.first][c.second] = false;
							} // end while
						} // end if
						exp = "";
					} // end if
				} // end for
			} // end if
		} // end rep
	} // end rep

	return true;
}

