      eqs.emplace_back(eq);
  }

  for (size_t j=0; j<W; ++j) {
    std::string eq="";
    for (size_t i=0; i<H; ++i) {
      if (g[i][j] == '#') {
        if (eq.size() >= 3) {
          eqs.emplace_back(eq);
        }
        eq = "";
