bool compute(int x){
  calcStr a,b;
  a.s=w[x];
  b.s=w2[x];
  for(int i=0;i<(int)a.s.size();i++){
    if('A'<=a.s[i]&&a.s[i]<='Z')a.s[i]=u[a.s[i]-'A'];
