    while (cin >> H >> W, H) {
        for (int i=0; i<12; ++i) {
            for (int j=0; j<12; ++j) {
                pz[i][j] = '#';
            }
        }
        bx.clear(); by.clear();
        for (int i=1; i<=H; ++i) {
            for (int j=1; j<=W; ++j) {
                cin >> pz[i][j];
                if (pz[i][j] == '.') {
                    bx.push_back(j);
                    by.push_back(i);
                }
            }
        }
