        char c;
        for (int i=0; i<n; ++i) {
            cin >> c;
            ch.push_back(c);
        }
        sort(ch.begin(), ch.end(), cmb);
        memset(used, false, sizeof used);
        cout << solve(0, 0) << endl;
    }
    return 0;
}

