int eval(char *expr,int s,int t)
{
	int p=t-1;
	for(;p>=s;p--)if(expr[p]=='+'||expr[p]=='-')break;
	if(p>=s)
	{
		int a=eval(expr,s,p),b=eval(expr,p+1,t);
		if(a==inf||b==inf)return inf;
		return expr[p]=='+'?a+b:a-b;
	}
	p=t-1;
	for(;p>=s;p--)if(expr[p]=='*'||expr[p]=='/')break;
	if(p>=s)
	{
		int a=eval(expr,s,p),b=eval(expr,p+1,t);
		if(a==inf||b==inf)return inf;
		if(expr[p]=='/'&&(b==0||a%b!=0))return inf;
		return expr[p]=='*'?a*b:a/b;
	}

	if(t>s+1&&expr[s]=='0')return inf;
	int ret=0;
	for(p=t-1;p>=s;p--)ret*=10,ret+=expr[p]-'0';
	return ret;
}
bool ck2(bool tate,int y,int b,int x)
{
	char lhs[11]={0},rhs[11]={0}; int l=0,r=0;
	if(tate)
	{
		for(;b<y;b++)
		{
			if(bd[b][x]=='=')break;
			lhs[l++]=bd[b][x];
		}
		b++;
		for(;b<y;b++)rhs[r++]=bd[b][x];
	}
	else
	{
		for(;b<x;b++)
		{
			if(bd[y][b]=='=')break;
			lhs[l++]=bd[y][b];
		}
		b++;
		for(;b<x;b++)rhs[r++]=bd[y][b];
	}

	int a=eval(lhs,0,l); b=eval(rhs,0,r);
	return a!=inf&&b!=inf&&a==b;
}
bool ck()
{
	rep(i,h)
	{
		int beg=0;
		bool eq=0,dot=0;
		rep(j,w+1)
		{
			if(j==w||bd[i][j]=='#')
			{
				if(eq&&!dot)
