	}
	else
	{
		if(x==b)return ck()&&rec();
		if(bd[y][b]!='.')return rec2(0,y,b+1,x);
		rep(i,n)if(lst[i])
		{
			bd[y][b]=lst[i]; lst[i]=0;
			if(rec2(0,y,b+1,x))return 1;
			lst[i]=bd[y][b]; bd[y][b]='.';
		}
	}
	return 0;
}
