				eq=dot=0; beg=i+1;
			}
			else if(bd[i][j]=='=')eq=1;
			else if(bd[i][j]=='.')dot=1;
		}
	}

	rep(i,h)rep(j,w)if(bd[i][j]=='.')return 0;
	return ck();
}
