#define for_(i,a,b) for(int i=(a);i<(b);++i)

const int dx[4] = {-1,0,1,0};
const int dy[4] = {0,-1,0,1};

class Equation {
public:
	string A, B;
	Equation(string S) {
		int p = S.find("=");
		A = S.substr(0, p);
		B = S.substr(p+1);
		// cerr << A << " " << B << endl;
	}

	string convert(string S, string symbol_map) {
		for (char& c : S) if ('a' <= c && c <= 'z') c = symbol_map[c - 'a'];
		return S;
	}

	pair< int, bool > calculate(string S) {
		stack< char > stack_op;
		stack< int > stack_num;

		auto calc = [&]() {
			char op = stack_op.top(); stack_op.pop();
			int b = stack_num.top(); stack_num.pop();
			int a = stack_num.top(); stack_num.pop();
			//cerr << a << op << b << endl;
			if (op == '+') stack_num.push(a + b);
			if (op == '-') stack_num.push(a - b);
			if (op == '*') stack_num.push(a * b);
			if (op == '/') {
				if (b == 0) return false;
				if (a % b != 0) return false;
				stack_num.push(a / b);
			}
			return true;	
		};

		S += '#';
		int num = -1;
		for (char c : S) {
			if (isdigit(c)) {
				if (num == -1) num = 0;
				num = 10 * num + (c - '0');
			}
			else {
				stack_num.push(num);
				num = -1;
				while (!stack_op.empty() && (stack_op.top() == '*' || stack_op.top() == '/')) {
					if (!calc()) return pair< int, bool >(0, false);
				}
				if (c != '#') stack_op.push(c);
			}
		}

		while (!stack_op.empty()) {
			if (!calc()) return pair< int, bool >(0, false);
		}
		return pair< int, bool >(stack_num.top(), true);
	}

	bool valid(string S) {
		int len = S.size();
		bool num_flag = false, op_flag = false;
		for_(i,0,len) {
			char c = S[i];
			if (isdigit(c)) {
				if (!num_flag && c == '0' && i<len-1 && isdigit(S[i+1])) return false; // reading zero
				num_flag = true;
				op_flag = false;
			}
			else {
				if (!num_flag || op_flag) return false; // unary operation
				num_flag = false;
				op_flag = true;
			}
		}
		return num_flag;
	}

	bool evaluate(string symbol_map) {
		string X = convert(A, symbol_map);
		string Y = convert(B, symbol_map);
		if (!valid(X) || !valid(Y)) return false;
		pair< int, bool > pX = calculate(X), pY = calculate(Y);
		//cerr << X << " ? " << Y << " -> " << pX.first << " ? " << pY.first << endl;
		return  pX.second && pY.second && pX.first == pY.first;
	}
};

int H, W, n, buf[11][11];
string board[11];
char symbol[11];

vector< Equation > findEquations() {
	vector< Equation > ret;

	for_(d,0,2) {
		memset(buf, 0, sizeof(buf));
		for_(y,0,H) for_(x,0,W) {
			if (board[y][x] != '#' && buf[y][x] == 0) {
				string S = "";
				int cx = x, cy = y;
				while (cy < H && cx < W && board[cy][cx] != '#') {
					S += board[cy][cx];
					buf[cy][cx] = 1;
					if (d == 0) ++cy;
					else ++cx;
				}
				if (S.size() >= 3) {
					ret.push_back(Equation(S));
					//cerr << S << endl;
				}
			}
		}
	}

	return ret;
}

bool solve() {
	int a = 0;
	for_(y,0,H) {
		cin >> board[y];
		for_(x,0,W) {
			if (board[y][x] == '.') {
				board[y][x] = char('a' + a);
				++a;
			}
		}
	}

	cin >> n;
	for_(i,0,n) cin >> symbol[i];
	sort(symbol, symbol + n);

	if (a != n) return false;

	vector< Equation > eqs = findEquations();

	do {
		string s_map = "";
		for_(i,0,n) s_map += symbol[i];
		//cerr << s_map << endl;
		bool flag = true;
		for (Equation eq : eqs) flag &= eq.evaluate(s_map);
		if (flag) return true;
	} while (next_permutation(symbol, symbol + n));

	return false;
}

int main() {
	while (cin >> H >> W, H) {
		puts(solve() ? "Yes" : "No");
	}
}

#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long LL;
typedef pair<int, LL> Result;
class ParseError{};
struct Parser{
    Result expr(string s, int p){
        Result r = term(s, p);
        while(s[r.first] == '+' || s[r.first] == '-'){
            Result t = term(s, r.first + 1);
            if(s[r.first] == '+'){
                r.second += t.second;
            }else{
                r.second -= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result term(string s, int p){
        Result r = factor(s, p);
        while(s[r.first] == '*' || s[r.first] == '/'){
            Result t = factor(s, r.first + 1);
            if(s[r.first] == '*'){
                r.second *= t.second;
            }else{
                if(t.second == 0) throw ParseError();
                r.second /= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result factor(string s, int p){
        if(s[p] == '0'){
            if(isdigit(s[p + 1])) throw ParseError();
            return Result(p + 1, 0);
        }else if(isdigit(s[p])){
            LL num = 0;
            while(isdigit(s[p])){
                num = num * 10 + s[p++] - '0';
            }
            return Result(p, num);
        }else{
            throw ParseError();
        }
    }
};
LL parse(string s){
    LL res = Parser().expr(s, 0).second;
    //cout << s << " " << res << endl;
    return res;
}
char select_ch[10];
bool used[10];
int H, W, L;
int iter;
bool dfs(int k, vector<char>& vc, vector<string> check[10]){
    if(++iter > 100000) return false;
    if(k == L) return true;
    REP(i, vc.size()) if(!used[i]){
        select_ch[k] = vc[i];
        used[i] = true;
        for(auto& s : check[k]){
            string s1, s2;
            for(auto c : s) {
                if(islower(c)){
                    c = select_ch[ c - 'a' ];
                }
                if(c == '='){
                    if(s2.size() > 0) goto NEXT;
                    swap(s1, s2);
                }else{
                    s1 += c;
                }
            }
            try{
                if(parse(s1) != parse(s2)) goto NEXT;
            }catch(ParseError e){
                goto NEXT;
            }
        }
        if(dfs(k + 1, vc, check)) return true;
        NEXT:
        used[i] = false;
    }
    return false;
}
