constexpr int INF=1e9;
constexpr int MOD=1e9+7;
constexpr double EPS=1e-9;

int expr(const string& s)
{
	int x; char op1;
	for(int i=0,j=0,k=0;i<s.size();i++){
		int y=0; char op2;
		for(j=i;j<s.size();j++){
			if(s[j]<'1' || '9'<s[j]) return INF;
			int z=0;
			for(k=j;k<s.size() && isdigit(s[k]);k++)
				z=z*10+s[k]-'0';
			if(j==i) y=z;
			else if(op2=='*') y*=z;
			else if(z && y%z==0) y/=z;
			else return INF;
			j=k;
			if(i==s.size()) break;
			if(s[j]!='*' && s[j]!='/') break;
			op2=s[j];
		}
		if(i==0) x=y;
		else op1=='+'?x+=y:x-=y;
		i=j;
		if(i==s.size()) break;
		if(s[i]!='+' && s[i]!='-') return INF;
		op1=s[i];
	}
	return x;
}

bool check(const vs& grid,const vector<pii>& f)
{
	string s;
	for(auto p:f) s+=grid[p.first][p.second];
	if(count(all(s),'=')!=1) return false;
	int eq=s.find('=');
	if(eq==0 || eq==s.size()-1) return false;
	string a=s.substr(0,eq),b=s.substr(eq+1);

	int x=expr(a),y=expr(b);
	if(x==INF || y==INF || x!=y)
		return false;
	return true;
}

int main()
{
	for(int h,w;cin>>h>>w && h|w;){
		vs grid(h);
		rep(i,h) cin>>grid[i];
		int n; cin>>n;
		string cs(n,'~');
		rep(i,n) cin>>cs[i];

		vector<pii> ps;
		vector<vector<pii>> fs;
		rep(i,h) rep(j,w){
			if(grid[i][j]=='.') ps.emplace_back(i,j);
			if(grid[i][j]!='#'){
				if((i==0 || grid[i-1][j]=='#') && i<h-1 && grid[i+1][j]!='#'){
					vector<pii> f;
					for(int k=i;k<h && grid[k][j]!='#';k++)
						f.emplace_back(k,j);
					fs.emplace_back(f);
				}
				if((j==0 || grid[i][j-1]=='#') && j<w-1 && grid[i][j+1]!='#'){
					vector<pii> f;
					for(int k=j;k<w && grid[i][k]!='#';k++)
						f.emplace_back(i,k);
					fs.emplace_back(f);
				}
			}
		}

		vvi ds(n+1);
		{
			vi vis(fs.size());
			rep(i,n+1){
				rep(j,fs.size()) if(!vis[j]){
					string s;
					for(auto p:fs[j]) s+=grid[p.first][p.second];
					if(count(all(s),'.')==0){
						ds[i].push_back(j);
						vis[j]=1;
					}
				}
				if(i<n) grid[ps[i].first][ps[i].second]='?';
			}
			rep(i,n)
				grid[ps[i].first][ps[i].second]='.';
			rotate(begin(ds),1+all(ds));
		}

		string res="No";
		{
			bool ok=true;
			for(auto i:ds[n])
				ok&=check(grid,fs[i]);
			if(!ok){
				cout<<"No"<<endl;
				continue;
			}
		}

		vi is(n); iota(all(is),0);
		do{
			int i=0;
			for(;i<n;i++){
				grid[ps[i].first][ps[i].second]=cs[is[i]];
				bool ok=all_of(all(ds[i]),[&](int j){return check(grid,fs[j]);});
				if(!ok) break;
			}
			if(i==n){
				res="Yes";
				break;
			}
			reverse(i+1+all(is));
		}while(next_permutation(all(is)));
		cout<<res<<endl;
	}
}

