      v.push_back(pii(y,x));
      id.push_back(idx);
    }
    s[idx++] = a[y][x];
    y += dy;
    x += dx;
  }
  if (idx >= 2) {
    s[idx] = 0;
    if (v.size() == 0 && !check(s)) return 0;
    dotNum[eqNum] = v.size();
    strcpy(eq[eqNum], s);
    REP(i,v.size()) {
