      if (!inserted[y][x]) {
        pos[posNum++] = pii(y,x);
        inserted[y][x] = 1;
      }
    }
    eqNum++;
  }
  return 1;
}

bool checkOp(int id, int idx) {
  int n = strlen(eq[id]);
  if (idx && isoperator(eq[id][idx-1])) return 1;
  if (idx+1<n && isoperator(eq[id][idx+1])) return 1;
  return 0;
}

