bool dfs(int S, int now) {
  if (now == posNum) return 1;
  int y = pos[now].first;
  int x = pos[now].second;
  FOR(it, ididx[y][x]) {
    dotNum[it->first]--;
  }
  REP(i,n) {
    if (S>>i&1) continue;
    if (!isdigit(b[i]) && numOnly[now]) continue;
    bool f = 1;
    FOR(it, ididx[y][x]) {
      int id = it->first;
      int idx = it->second;
      eq[id][idx] = b[i];
      if (dotNum[id] == 0) {
        f &= check(eq[id]);
      }
    }
    if (f) {
      if (dfs(S|1<<i,now+1)) return 1;
    }
  }
  FOR(it, ididx[y][x]) {
    eq[it->first][it->second] = '.';
    dotNum[it->first]++;
  }
  return 0;
}

