int N(){
	if(!('1'<=s[idx] && s[idx]<='9')){ res=false; ASRT; }
	int a=0;
	while(isdigit(s[idx])) a=10*a+s[idx++]-'0';
	return a;
}

int N0(){
	int a;
	if(s[idx]=='0'){
		idx++;
		a=0;
	}
	else a=N();
	ASRT;
	return a;
}

int Ex(){
	int a=N0();
	ASRT;
	while(s[idx]=='+' || s[idx]=='-' || s[idx]=='*' || s[idx]=='/'){
		char op=s[idx++];
		int b=N0();
		ASRT;
		if(op=='/' && (b==0 || a%b!=0)){ res=false; ASRT; }

		if(op=='+') a+=b;
		if(op=='-') a-=b;
		if(op=='*') a*=b;
		if(op=='/') a/=b;
	}
	return a;
}

int Eq(){
	int a=Ex();
	ASRT;
	if(s[idx++]!='='){ res=false; ASRT; }
	int b=Ex();
	ASRT;
	if(a!=b || s[idx]) res=false;
