    vector<int> perm;
    for(int i = 0; i < vc.size(); i++) if(!used[i]) perm.push_back(i);
    random_shuffle(perm.begin(), perm.end());
    for(auto i : perm) {
        select_ch[k] = vc[i];
        used[i] = true;
        for(auto& s : check[k]){
            string s1, s2;
            for(auto c : s) {
                if(islower(c)){
                    c = select_ch[ c - 'a' ];
                }
                if(c == '='){
                    if(s2.size() > 0) goto NEXT;
                    swap(s1, s2);
                }else{
                    s1 += c;
                }
            }
            try{
                if(parse(s1) != parse(s2)) goto NEXT;
            }catch(ParseError e){
                goto NEXT;
            }
        }
        if(dfs(k + 1, vc, check)) return true;
        NEXT:
        used[i] = false;
    }
    return false;
}
