pii number(const string& s,int i)
{
	//dump(mt(__PRETTY_FUNCTION__,s,i));
	if(s[i]<'1' || '9'<s[i]) throw "number";
	int x=0;
	for(;i<s.size() && isdigit(s[i]);i++)
		x=x*10+s[i]-'0';
	return mp(x,i);
}
pii term(const string& s,int i)
{
	int x; tie(x,i)=number(s,i);
	while(i<s.size()){
		if(s[i]=='+' || s[i]=='-') break;
		if(s[i]!='*' && s[i]!='/') throw "*/";
		char op=s[i];
		int y; tie(y,i)=number(s,i+1);
		if(op=='*') x*=y;
		else{
			if(y==0 || x%y) throw "/";
			x/=y;
		}
	}
	return mp(x,i);
}
int expr(const string& s)
{
	int x,i; tie(x,i)=term(s,0);
	while(i<s.size()){
		if(s[i]!='+' && s[i]!='-') throw "+-";
		char op=s[i];
		int y; tie(y,i)=term(s,i+1);
		op=='+'?x+=y:x-=y;
	}
	return x;
}

bool check(const vs& grid,const vector<vector<pii>>& fs)
{
	for(auto f:fs){
		string s;
		for(auto p:f) s+=grid[p.first][p.second];
		if(count(all(s),'=')!=1) return false;
		int eq=s.find('=');
		if(eq==0 || eq==s.size()-1) return false;
		string a=s.substr(0,eq),b=s.substr(eq+1);
		try{
			if(expr(a)!=expr(b))
				return false;
		}
		catch(const char* msg){
			return false;
		}
	}
	return true;
}

