        bs[i] = true;

        change[x] = p[i];
        bool ok = true;
        for(unsigned j=0; j<formula[x].size(); ++j){
            string t = formula[x][j];
            for(unsigned k=0; k<t.size(); ++k){
                if(isalpha(t[k]))
                    t[k] = change[t[k]-'a'];
            }
            if(!check(t))
                ok = false;
        }

        if(ok && solve(x+1, bs))
            return true;

        bs[i] = false;
    }

    return false;
}

