typedef long long int lli;

using namespace std;

const int H = 10 + 1;
const int W = 10 + 1;

int total = 0;

char g[H][W];

int calc(string s)
{
  istringstream iss( s );
  vector<int> num;
  vector<char> op;
  int n;
  char c;
  while( iss >> n ){    
    num.push_back( n );
    if( iss >> c )op.push_back( c );
    else break;
  }

  for(int i=0; i<op.size(); ++i){
    if( op[i] == '+' || op[i] == '-' ) continue;
    else if( op[i] == '*' ) num[i] *= num[i+1];
    else if( op[i] == '/' ) num[i] /= num[i+1];
    else assert( false && "invalid op" );
    num.erase( num.begin() + i + 1);
    op.erase( op.begin() + i );
    --i;
  }

  int ret = num[0];
  for(int i=0; i<op.size(); ++i){
    if( op[i] == '+' )ret += num[i+1];
    if( op[i] == '-' )ret -= num[i+1];     
  }

  return ret;
}

inline bool is_op(char c){ return !isdigit(c); }

bool check_syntax(string s)
{
  if( count( s.begin(), s.end(), '=' ) != 1 )return false;
  string t[2];
  for(int i = 0, j=0; i<s.size(); ++i){
    if( s[i] == '=' ){ ++j; continue; }
    t[j] += s[i];
  }

  if( t[0].size() == 0 )return false;
  if( t[1].size() == 0 )return false;

  for(int i=0; i<2; ++i){
    for(int j=0; j<t[i].size(); ++j){
      if( !is_op(t[i][j]) ) continue;
      if( j == 0 || j+1 == t[i].size() )return false;
      if( is_op( t[i][j-1] ) || is_op( t[i][j+1] ) )return false;
    }
  }

  return calc(t[0]) == calc(t[1]);
}


struct F{
  vector< pair<int, int> > v;
  F(){ v.clear(); }
};

string build(F f)
{
  string s;
  for(int i=0; i<f.v.size(); ++i){
    s += g[ f.v[i].first ][ f.v[i].second ];
  }
  return s;
}

lli cpy(char c)
{
  if( isdigit(c) ) return c - '0';
  if( c == '+' ) return 10;
  if( c == '*' ) return 11;
  if( c == '/' ) return 12;
  if( c == '-' ) return 13;
  return -(1LL << 60);
}

bool solve(char p[], int size, int h, int w)
{
  for(int i=0; i<h; ++i){
    for(int j=0; j<w; ++j){
      if( g[i][j] == '#' ) continue;
      const int di[] = {0, 0, -1, 1};
      const int dj[] = {-1, 1, 0, 0};
      bool flg = false;
      for(int d=0; d<4 && !flg; ++d){
	int ni = i + di[d];
	int nj = j + dj[d];
	if( ni < 0 || nj < 0 )continue;
	if( h <= ni || w <= nj )continue;
	flg = g[ni][nj] != '#';
      }
      if( !flg )return false;
    }
  }

  static pair<int, int> emp[10 * 10 + 1];
  int emp_size = 0;
  for(int i=0; i<h; ++i){
    for(int j=0; j<w; ++j){
      if( g[i][j] == '.' ) emp[emp_size++] = make_pair(i, j);
    }
  }

  if( emp_size != size )return false;

  static char puz[H][W];
  static F v[1000];  

  fill( v, v + 1000, F() );

  int v_size = 0;

  copy( g[0], g[H], puz[0] );
  for(int i=0; i<h; ++i){
    for(int j=0; j<w; ++j){
      if( puz[i][j] == '#' )continue;

      bool filled = true;
      F f;
      for(int l=0; ; ++l){
	int ni = i;
	int nj = j + l;
	if( h <= ni || w <= nj )break;
	if( puz[ni][nj] == '#' )break;
	f.v.push_back( make_pair(ni, nj) );
	if( puz[ni][nj] == '.' ) filled = false;
	puz[ni][nj] = '#';
      }
      if( 1 < f.v.size() ){
	if( !filled ) v[v_size++] = f;
	else{
	  if( !check_syntax( build( f ) ) )return false;
	}
      }
    }
  }  

  if( v_size == 0 )return true;

  copy( g[0], g[H], puz[0] );
  for(int i=0; i<h; ++i){
    for(int j=0; j<w; ++j){
      if( puz[i][j] == '#' )continue;

      bool filled = true;
      F f;
      for(int l=0; ; ++l){
	int ni = i + l;
	int nj = j;
	if( h <= ni || w <= nj )break;
	if( puz[ni][nj] == '#' )break;
	f.v.push_back( make_pair(ni, nj) );
	if( puz[ni][nj] == '.' ) filled = false;
	puz[ni][nj] = '#';
      }
      if( 1 < f.v.size() ){
	if( !filled ) v[v_size++] = f;
	else{
	  if( !check_syntax( build( f ) ) )return false;
	}
      }
    }
  }

  sort( p, p + size );
  set<lli> T, F;
  do{

    for(int i=0; i<emp_size; ++i){
      g[ emp[i].first ][ emp[i].second ] = p[i];
    }
    bool flg = true;
    for(int i=0; i<v_size && flg; ++i){
      string s;
      lli h = 0;
      for(int j=0; j<v[i].v.size(); ++j){
	int a = v[i].v[j].first;
	int b = v[i].v[j].second;
	s += g[a][b];
	h = (h + cpy( g[a][b] )) * 19LL;
	h %= 1LL << 60;
      }
      if( F.count( h ) ) flg = false;
      else if( T.count( h ) ) flg = true;
      else if( !check_syntax(s) ){
	F.insert( h );
	flg = false;
      }
      else T.insert( h );
    }    
    if( flg ) return true;

  }while( next_permutation( p, p + size ) );

  return false;
}

int main(void)
{
  int h, w;
  while( scanf("%d%d\n", &h, &w) != EOF && (h | w) ){
    for(int i=0; i<h; ++i){
      for(int j=0; j<w; ++j){
	scanf("%c", &g[i][j]);
      }
