typedef pair<const char *, int > parsed;
typedef pair<bool,int> bnfm;

parsed expr(const char *);
parsed term(const char *);
parsed fact(const char *);

bnfm Ex(int pos, const string &s);
bnfm N0(int pos, const string &s);

parsed expr(const char *p){
  parsed r = term(p);
  if( r.first == NULL ) return r; // parse error
  while( *(r.first)=='+' || *(r.first)=='-' ){
    const char *op = r.first;
    parsed l = term(op+1);
    if( l.first == NULL ) return l;

    if( *op=='+' ){
      r.first = l.first;
      r.second = r.second + l.second;
    }else{
      r.first = l.first;
      r.second = r.second - l.second;
    }
  }
  return r;
}

parsed term(const char *p){
  parsed r = fact(p);
  if( r.first == NULL ) return r;
  while( *(r.first)=='*' || *(r.first)=='/' ){
    const char *op = r.first;
    parsed l = fact(op+1);
    if( l.first == NULL ) return l;

    if( *op=='*' ){
      r.first = l.first;
      r.second = r.second * l.second;
    }else{
      r.first = l.first;
      if( l.second == 0 || r.second % l.second != 0 ) return parsed(NULL,-1);
      r.second = int(r.second / l.second);
    }
  }
  return r;
}

parsed fact(const char *p){
  if( *p=='(' ){
    // new expression
    parsed tp = expr(p+1);
    if( tp.first == NULL ) return tp;
    return parsed(tp.first+1, tp.second);
  }
  if( *p=='-' ){
    // negative fact
    parsed tp = fact(p+1);
    if( tp.first == NULL ) return tp;
    tp.second *= -1;
    return tp;
  }
  if( isdigit(*p) ){
    // number
    int num = *p - '0';
    while( isdigit(*(++p)) ){
      num *= 10;
      num += *p - '0';
    }
    return parsed(p,num);
  }else assert(0); return parsed(NULL,0);
}

bool Eq(const string &s){
  int pos = 0;
  bnfm r = Ex(pos,s);
  if( !r.first ) return false;
  pos = r.second;
  if( pos >= s.length() ) return false;
  if( s[pos] != '=' ) return false;
  ++pos;
  r = Ex(pos,s);
  if( !r.first ) return false;
  if( r.second < s.length() ) return false;
  return true;
}

bnfm Ex(int pos, const string &s){
  if( pos >= s.length() ) return bnfm(false,pos);
  bnfm r = N0(pos,s);
  if( !r.first ) return bnfm(false,r.second);
  pos = r.second;
  if( pos >= s.length() ) return bnfm(true,pos);
  while( s[ pos ] == '+' || s[ pos ] == '-' || s[ pos ] == '*' || s[ pos ] == '/' ){
    bnfm r = N0( pos+1, s );
    if( !r.first ) return bnfm(false,r.second);
    pos = r.second;
    if( pos >= s.length() ) break;
  }
  return bnfm(true,pos);
}

bnfm N0(int pos, const string &s){
  if( pos >= s.length() ) return bnfm( false,pos );
  if( !isdigit( s[pos] ) ) return bnfm(false,pos+1);
  if( s[pos] == '0' ){
    if( pos + 1 < s.length() ){
      if( isdigit( s[pos+1] ) ) return bnfm(false,pos+2);
    }
    return bnfm(true,pos+1);
  }else{
    while( isdigit(s[pos]) ){
      ++pos;
      if( pos >= s.length() ) break;
    }
    return bnfm(true,pos);
  }
}

bool Evaluate(const string &s){
  string s1,s2;
  int i,j;
  for(i = 0; i < s.length(); ++i){
    if( s[i] == '=' ) break;
    else s1 += s[i];
  }
  for(j=i+1; j < s.length(); ++j){
    s2 += s[j];
  }
  parsed p1 = expr(s1.c_str());
  parsed p2 = expr(s2.c_str());
  if( p1.first == NULL || p2.first == NULL ) return false;
  if( p1.second == p2.second ) return true;
  else return false;
}

#define NMAX 11

bool Judge(int H, int W, char pzl[][MAX]){
  vector<string> eqs;
  const int di [] = {-1,0,1,0};
  const int dj [] = {0,1,0,-1};

  for(int i = 0; i < H; ++i){
    for(int j = 0; j < W; ++j){
      if( pzl[i+1][j+1] != '#' ){ 
	for(int l = 0; l < sizeof(di)/sizeof(*di); ++l){
	  if( l == 1 || l == 2 ) continue;
	  int ti = i + 1 + di[l]; int tj = j + 1 + dj[l];
	  if( pzl[ti][tj] == '#' ){
	    int dir = (l+2)%(sizeof(di)/sizeof(*di));
	    ti = i+1+di[dir]; tj = j+1+dj[dir];
	    string eq;
	    eq += pzl[i+1][j+1];
	    while(pzl[ti][tj]!='#'){
	      eq += pzl[ti][tj]; ti += di[dir]; tj += dj[dir];
	    }
	    if( eq.length() >= 3 && eq.find('.') == string::npos ){
	      if( !Eq(eq) ) return false;
	      if( !Evaluate( eq ) ) return false;
	      eqs.push_back( eq );
	    }
	    eq.clear();
	  }
	}
      }
    }
  }
  return true;
}

bool dfs(int n, int now, const vector<pair<int,int> > &hole_pos,
	 bool vis[NMAX], char holes[NMAX], int H, int W, char pzl[][MAX] ){

  if( !Judge(H,W,pzl) ){
    return false;
  }else if( now == hole_pos.size() ){
    return true;
  }

  for(int i = 0; i < n; ++i){
    if( !vis[i] ){
      vis[i]=true;
      pair<int,int> pi = hole_pos[ now ];
      char tmp = pzl[pi.first][pi.second];
      pzl[pi.first][pi.second] = holes[ i ];

      if( dfs( n, now+1, hole_pos, vis, holes, H,W, pzl ) ) return true;

      pzl[pi.first][pi.second] = tmp;
      vis[i]=false;
    }
  }
  return false;
}

int main(void){
  while(true){
    int H,W;
    char pzl[MAX][MAX];
    bool vis[NMAX]={false,};
    vector< pair<int,int> > hole_pos;
    for(int i = 0; i < MAX; ++i)for(int j = 0; j < MAX; ++j)pzl[i][j]='#';

    cin >> H >> W;
    if( H == 0 && W == 0) break;
    for(int i = 0; i < H; ++i){
      for(int j = 0; j < W; ++j){
	cin >> pzl[i+1][j+1];
	if(pzl[i+1][j+1] == '.' ){
	  hole_pos.push_back( make_pair(i+1,j+1) );
	}
      }
    }

    int n,num=0;
    cin >> n;
    int p[n];
    char holes[n];
    for(int i = 0; i < n; ++i){
      char d; cin >> d; holes[num++] = d; p[i] = i;
    }

    if( dfs(n,0,hole_pos,vis,holes,H,W,pzl) ){
      cout << "Yes" << endl;
    }else{
      cout << "No" <<endl;
    }


  }
  return 0;
}

