using namespace std;

typedef pair<int, int> P;

vector<P> V;
int H, W, N;
char M[10][10], T[10];
bool flag;

const int INF = 1<<24;
const int dx[] = {1,0,-1,0};
const int dy[] = {0,1,0,-1};

int getNum(string &s, int &pos) {
  int st = pos;
  int l = 0;
  if(!isdigit(s[pos])) {
    pos = s.size();
    flag = true;
    return INF;
  }
  while(pos < s.size() && isdigit(s[pos])) {
    //    num += s[pos++];
    ++pos;
    ++l;
  }
  //if(num[0] == '0') {
  if(s[st] == '0') {
    pos = s.size();
    flag = true;
    return INF;
  }
  return atoi(s.substr(st, l).c_str());
}


int term(string &s, int &pos) {
  int value = getNum(s, pos);
  if(value == INF) return INF;
  int x = value;
  while(pos < s.size()) {
    if(s[pos] == '*') {
      ++pos;
      value = getNum(s, pos);
      if(value == INF) return INF;
      x *= value;
    } else if(s[pos] == '/') {
      ++pos;
      int tmp = getNum(s, pos);
      if(tmp == INF) return INF;
      if(tmp == 0 || x % tmp != 0) {
	flag = true;
	return INF;
      }
      x /= tmp;
    } else {
      break;
    }
  }
  return x;
}

int exp(string &s, int &pos) {
  int value = term(s, pos);
  if(value == INF) return INF;
  int x = value;
  while(pos < s.size()) {
    if(s[pos] == '+') {
      ++pos;
      value = term(s, pos);
      if(value == INF) return INF;
      x += value;
    } else if(s[pos] == '-') {
      ++pos;
      value = term(s, pos);
      if(value == INF) return INF;
      x -= value;
    }else {
      break;
    }
  }
  return x;
}

int check(int x, int y, int dir) {
  string s[2];
  s[0] = s[1] = "";
  int p = 0;
  int a, b;

  do {
    if(M[y][x] == '=') {
      ++p;
    } else {
      s[p] += M[y][x];
    }
    x += dx[dir];
    y += dy[dir];
  } while(x < W && y < H);
  for(int i = 0; i < 2; ++i)
    if(!isdigit(s[i][0]) || !isdigit(s[i][(int)s[i].size()-1])) return false;

  int pos;
  flag = false;

  pos = 0;
  a = exp(s[0], pos);
  pos = 0;
  b = exp(s[1], pos);

  return !flag && a == b;
}

bool judge() {
  for(int i = 0; i < H; ++i) {
    for(int j = 0; j < W; ++j) {
      if((j == 0 || M[i][j-1] == '#') &&
	 M[i][j] != '#' && j+2 < W && M[i][j+1] != '#' && M[i][j+2] != '#') {
	if(!check(j, i, 0)) return false;

      }
      if((i == 0 || M[i-1][j] == '#') &&
	 M[i][j] != '#' && i+2 < H && M[i+1][j] != '#' && M[i+2][j] != '#') {
	if(!check(j, i, 1)) return false;
      }
    }
  }
  return true;
}

bool rec(int t) {
  if(t == N) {
    return judge();
  }

  for(int i = 0; i < N; ++i) {
    int x = V[i].first;
    int y = V[i].second;
    if(M[y][x] != '.') continue;
    int type = -1; // 0:Num, 1:Operator
    for(int i = 0; i < 4; ++i) {
      int nx = x + dx[i];
      int ny = y + dy[i];
      if(nx < 0 || nx >= W) continue;
      if(ny < 0 || ny >= H) continue;
      if(M[ny][nx] != '#' && M[ny][nx] != '.') {
	if(!isdigit(M[ny][nx])) {
	  type = 1;
	  break;
	}
      }
    }
    if(!isdigit(T[t]) && type == 1) continue;

    M[y][x] = T[t];
    if(rec(t+1)) return true;
    M[y][x] = '.';
  }
  return false;
}

int main() {
  while((cin >> H >> W) && (H || W)) {
    V.clear();
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
	cin >> M[i][j];
	if(M[i][j] == '.') V.push_back(P(j, i));
      }
    }

    cin >> N;
    for(int i = 0; i < N; ++i) cin >> T[i];
    sort(T, T+N);
    if(rec(0)) cout << "Yes" << endl;
    else        cout << "No" << endl;
  }
  return 0;
}

#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;

struct invalid {};
typedef const char *Iterator;

int eval_factor(Iterator& it, const Iterator& last)
{
  if (it == last) {
    throw invalid();
  }
  if (*it < '0' || '9' < *it) {
    throw invalid();
  }
  int val = *it - '0';
  ++it;
  if (val == 0) {
    return val;
  }
  while (it != last && '0' <= *it && *it <= '9') {
    val = 10*val + (*it - '0');
    ++it;
  }
  return val;
}

int eval_term(Iterator& it, const Iterator& last)
{
  int val = eval_factor(it, last);
  while (it != last && (*it == '*' || *it == '/')) {
    const char op = *it;
    ++it;
    const int x = eval_factor(it, last);
    if (op == '*') {
      val *= x;
    } else {
      val /= x;
    }
  }
  return val;
}

int eval_expr(Iterator& it, const Iterator& last)
{
  int val = eval_term(it, last);
  while (it != last && (*it == '+' || *it == '-')) {
    const char op = *it;
    ++it;
    const int x = eval_term(it, last);
    if (op == '+') {
      val += x;
    } else {
      val -= x;
    }
  }
  return val;
}

bool check(Iterator it, const Iterator& last) try
{
  const int l = eval_expr(it, last);
  if (it == last || *it != '=') {
    return false;
  }
  ++it;
  const int r = eval_expr(it, last);
  return it == last && l == r;
} catch (const invalid&) {
  return false;
}

static const int M = 15;

bool extract_expr(char (*dst)[M], pair<int,int> (*tbl)[2], const char rows[M][M], int H, int W, int& s)
{
  for (int i = 0; i < H; i++) {
    for (int j = 0; j < W; j++) {
      if (j == 0 || rows[i][j-1] == '#') {
        const int k = j;
        ++j;
        while (j < W && rows[i][j] != '#') {
          ++j;
        }
        if (j-k > 2) {
          bool b = false;
          for (int l = k; l < j; l++) {
            dst[s][l] = rows[i][l];
            if (rows[i][l] >= 'a') {
              b = true;
              pair<int,int> *t = tbl[rows[i][l]-'a'];
              if (t[0].first != -1) {
                t[1].first = s;
                t[1].second = l;
              } else {
                t[0].first = s;
                t[0].second = l;
              }
            }
          }
          if (b) {
            dst[s][j-k] = '\0';
            ++s;
          }
        }
      }
    }
  }
  return true;
}

bool solve(char (*exprs)[M], int s, char *cards, int N, const pair<int,int> (*tbl)[2])
{
  do {
    for (int i = 0; i < N; i++) {
      if (tbl[i][0].first >= 0) {
        exprs[tbl[i][0].first][tbl[i][0].second] = cards[i];
      }
      if (tbl[i][1].first >= 0) {
        exprs[tbl[i][1].first][tbl[i][1].second] = cards[i];
      }
    }
    for (int i = 0; i < s; i++) {
      if (!check(exprs[i], exprs[i]+strlen(exprs[i]))) {
        goto FAIL;
      }
    }
    return true;
FAIL:
    ;
  } while (next_permutation(cards, cards+N));
  return false;
}

int main()
{
  int H, W;
  while (scanf("%d %d", &H, &W) != EOF && H != 0) {
    char rows[M][M], cols[M][M];
    int n = 0;
    for (int i = 0; i < H; i++) {
      scanf("%s", rows[i]);
      for (int j = 0; j < W; j++) {
        if (rows[i][j] == '.') {
          rows[i][j] = 'a' + n;
          ++n;
        }
        cols[j][i] = rows[i][j];
        cols[j][H] = '\0';
      }
    }
    int N;
    scanf("%d", &N);
    char cards[M];
    for (int i = 0; i < N; i++) {
      char buf[4];
      scanf("%s", buf);
      cards[i] = buf[0];
    }
    sort(cards, cards+N);
    cards[N] = '\0';

    static char exprs[100][M];
    static pair<int,int> tbl[10][2];
    for (int i = 0; i < 10; i++) {
      tbl[i][0].first = tbl[i][1].second = -1;
    }
    int s = 0;
    if (!extract_expr(exprs, tbl, rows, H, W, s)) {
      puts("No");
      continue;
    }
    if (!extract_expr(exprs, tbl, cols, W, H, s)) {
      puts("No");
      continue;
    }
    if (s == 0) {
      puts("Yes");
      continue;
    }
    if (solve(exprs, s, cards, N, tbl)) {
      puts("Yes");
    } else {
      puts("No");
    }
  }
  return 0;
}

