            ++s;
          } else {
            if (!check(rows[i]+k, rows[i]+j)) {
              return false;
            }
          }
        }
      }
    }
  }
  return true;
}

bool solve(char (*exprs)[M], int s, char *cards, int N, const pair<int,int> (*tbl)[2])
{
  do {
    for (int i = 0; i < N; i++) {
      if (tbl[i][0].first >= 0) {
        exprs[tbl[i][0].first][tbl[i][0].second] = cards[i];
      }
      if (tbl[i][1].first >= 0) {
        exprs[tbl[i][1].first][tbl[i][1].second] = cards[i];
      }
    }
    for (int i = 0; i < s; i++) {
      if (!check(exprs[i], exprs[i]+strlen(exprs[i]))) {
        goto FAIL;
      }
    }
    return true;
FAIL:
    ;
  } while (next_permutation(cards, cards+N));
  return false;
}

int main()
{
  int H, W;
  while (scanf("%d %d", &H, &W) != EOF && H != 0) {
    char rows[M][M], cols[M][M];
    int n = 0;
    for (int i = 0; i < H; i++) {
      scanf("%s", rows[i]);
      for (int j = 0; j < W; j++) {
        if (rows[i][j] == '.') {
          rows[i][j] = 'a' + n;
          ++n;
        }
        cols[j][i] = rows[i][j];
        cols[j][H] = '\0';
      }
    }
    int N;
    scanf("%d", &N);
    char cards[M];
    for (int i = 0; i < N; i++) {
      char buf[4];
      scanf("%s", buf);
      cards[i] = buf[0];
    }
    sort(cards, cards+N);
    cards[N] = '\0';

    static char exprs[100][M];
    static pair<int,int> tbl[10][2];
    for (int i = 0; i < 10; i++) {
      tbl[i][0].first = tbl[i][1].second = -1;
    }
    int s = 0;
    if (!extract_expr(exprs, tbl, rows, H, W, s)) {
      puts("No");
      continue;
    }
    if (!extract_expr(exprs, tbl, cols, W, H, s)) {
      puts("No");
      continue;
    }
    if (s == 0) {
      puts("Yes");
      continue;
    }
    if (solve(exprs, s, cards, N, tbl)) {
      puts("Yes");
    } else {
      puts("No");
    }
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=(a);i<(b);++i)

const int dx[4] = {-1,0,1,0};
const int dy[4] = {0,-1,0,1};

class Equation {
public:
	string A, B;
	Equation(string S) {
		int p = S.find("=");
		A = S.substr(0, p);
		B = S.substr(p+1);
		// cerr << A << " " << B << endl;
	}

	string convert(string S, string symbol_map) {
		for (char& c : S) if ('a' <= c && c <= 'z') c = symbol_map[c - 'a'];
		return S;
	}

	pair< int, bool > calculate(string S) {
		stack< char > stack_op;
		stack< int > stack_num;

		auto calc = [&]() {
			char op = stack_op.top(); stack_op.pop();
			int b = stack_num.top(); stack_num.pop();
			int a = stack_num.top(); stack_num.pop();
			//cerr << a << op << b << endl;
			if (op == '+') stack_num.push(a + b);
			if (op == '-') stack_num.push(a - b);
			if (op == '*') stack_num.push(a * b);
			if (op == '/') {
				if (b == 0) return false;
				if (a % b != 0) return false;
				stack_num.push(a / b);
			}
			return true;	
		};

		S += '#';
		int num = -1;
		for (char c : S) {
			if (isdigit(c)) {
				if (num == -1) num = 0;
				num = 10 * num + (c - '0');
			}
			else {
				stack_num.push(num);
				num = -1;
				while (stack_op.empty() && (stack_op.top() == '*' || stack_op.top() == '/')) {
					if (!calc()) return pair< int, bool >(0, false);
				}
				if (c != '#') stack_op.push(c);
			}
		}

		while (!stack_op.empty()) {
			if (!calc()) return pair< int, bool >(0, false);
		}
		return pair< int, bool >(stack_num.top(), true);
	}

	bool valid(string S) {
		int len = S.size();
		bool num_flag = false, op_flag = false;
		for_(i,0,len) {
			char c = S[i];
			if (isdigit(c)) {
				if (!num_flag && c == '0' && i<len-1 && isdigit(S[i+1])) return false; // reading zero
				num_flag = true;
				op_flag = false;
			}
			else {
				if (!num_flag || op_flag) return false; // unary operation
				num_flag = false;
				op_flag = true;
			}
		}
		return num_flag;
	}

	bool evaluate(string symbol_map) {
		string X = convert(A, symbol_map);
		string Y = convert(B, symbol_map);
		if (!valid(X) || !valid(Y)) return false;
		pair< int, bool > pX = calculate(X), pY = calculate(Y);
		//cerr << X << " ? " << Y << " -> " << pX.first << " ? " << pY.first << endl;
		return  pX.second && pY.second && pX.first == pY.first;
	}
};

int H, W, n, buf[11][11];
string board[11];
char symbol[11];

vector< Equation > findEquations() {
	vector< Equation > ret;

	for_(d,0,2) {
		memset(buf, 0, sizeof(buf));
		for_(y,0,H) for_(x,0,W) {
			if (board[y][x] != '#' && buf[y][x] == 0) {
				string S = "";
				int cx = x, cy = y;
				while (cy < H && cx < W && board[cy][cx] != '#') {
					S += board[cy][cx];
					buf[cy][cx] = 1;
					if (d == 0) ++cy;
					else ++cx;
				}
				if (S.size() >= 3) {
					ret.push_back(Equation(S));
					//cerr << S << endl;
				}
			}
		}
	}

	return ret;
}

bool solve() {
	int a = 0;
	for_(y,0,H) {
		cin >> board[y];
		for_(x,0,W) {
			if (board[y][x] == '.') {
				board[y][x] = char('a' + a);
				++a;
			}
		}
	}

	cin >> n;
	for_(i,0,n) cin >> symbol[i];
	sort(symbol, symbol + n);

	if (a != n) return false;

	vector< Equation > eqs = findEquations();

	do {
		string s_map = "";
		for_(i,0,n) s_map += symbol[i];
		//cerr << s_map << endl;
		bool flag = true;
		for (Equation eq : eqs) flag &= eq.evaluate(s_map);
		if (flag) return true;
	} while (next_permutation(symbol, symbol + n));

	return false;
}

int main() {
	while (cin >> H >> W, H) {
		puts(solve() ? "Yes" : "No");
	}
}

