int next_perm(char a[],int n,int r)
{
  int i,j,min_p;
  char p,wk,min_;

  if(a[n-2]<a[n-1])
    {
	wk=a[n-1];
	a[n-1]=a[n-2];
	a[n-2]=wk;
	return(-1);
